INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ Cứng SSD SSTC Oceanic Whitetip MAX III 512GB M.2 NVME GEN3', 'o-cung-ssd-sstc-oceanic-whitetip-max-iii-512gb-m2-gen3', 50,
        '{"Dung lượng": "512 GB", "Chuẩn giao tiếp": "PCIe Gen 3", "Kích thước": "M.2 2280", "Tốc độ đọc": "Lên đến 3500MB/s", "Tốc độ ghi": "Lên đến 3150MB/s", "Độ bền (TBW)": "600TB"}',
        'Nâng tầm trải nghiệm máy tính của bạn lên một đẳng cấp hoàn toàn mới với Ổ Cứng SSD SSTC Oceanic Whitetip MAX III 512GB M.2 NVME GEN3. Lấy cảm hứng từ sự nhanh nhẹn và mạnh mẽ của đại dương, Oceanic Whitetip MAX III không chỉ là một ổ cứng lưu trữ, mà còn là trái tim hiệu năng đỉnh cao cho hệ thống của bạn. Với chuẩn giao tiếp NVMe PCIe Gen3 tiên tiến, ổ SSD này mang đến tốc độ đọc/ghi dữ liệu vượt trội so với các ổ SSD SATA truyền thống, giúp rút ngắn đáng kể thời gian khởi động hệ điều hành, tải ứng dụng chỉ trong tích tắc và chuyển đổi giữa các tác vụ đa nhiệm mượt mà không còn độ trễ. Dù bạn là game thủ chuyên nghiệp khao khát tốc độ tải màn hình tức thì, nhà sáng tạo nội dung cần xử lý các tệp lớn hiệu quả, hay đơn giản là người dùng muốn trải nghiệm sự mượt mà tối đa hàng ngày, Oceanic Whitetip MAX III sẽ đáp ứng mọi kỳ vọng. Dung lượng 512GB cung cấp không gian lý tưởng để cài đặt hệ điều hành, các phần mềm thiết yếu và những tựa game yêu thích của bạn, đảm bảo hiệu suất hoạt động luôn ở mức cao nhất mà không lo đầy bộ nhớ. Thiết kế chuẩn M.2 2280 nhỏ gọn giúp dễ dàng tích hợp vào hầu hết các bo mạch chủ hiện đại, từ máy tính để bàn gaming hầm hố đến những chiếc laptop siêu mỏng, tối ưu hóa không gian bên trong và cải thiện luồng khí. Bên cạnh tốc độ chóng mặt, ổ cứng SSTC Oceanic Whitetip MAX III còn nổi bật với độ bền vượt trội nhờ không có bộ phận chuyển động, hoạt động êm ái, ít tiêu thụ điện năng và tỏa nhiệt thấp hơn. Hãy sẵn sàng khai phá sức mạnh tiềm ẩn của cỗ máy của bạn và tận hưởng một kỷ nguyên hiệu năng không giới hạn cùng SSTC Oceanic Whitetip MAX III 512GB M.2 NVME GEN3.',
        1, 'https://product.hstatic.net/200000722513/product/sstc-oceanic-whitetip-nvme-m.2_0a0ba19d9f98496cb1d31696bbe52b16.png'
    );
