INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ Cứng SSD Samsung 970 Evo Plus 250Gb PCIe NVMe M.2', 'ssd-samsung-970-evo-plus-250gb-m-2-nvme', 16,
        '{"Nhà sản xuất": "Samsung", "Model": "MZ-V7S250BW", "Chuẩn giao tiếp": "PCIe Gen 3.0 x 4, NVMe 1.3", "Kích thước": "M.2 2280", "Dung lượng": "250 GB", "Tốc độ đọc": "3500 MB/s", "Tốc độ ghi": "2300 MB/s", "NAND Flash": "Samsung V-NAND 3-bit MLC", "TBW": "150TB"}',
        'Tốc độ đọc ghi nhanhỔ cứng SSD Samsung 970 EVO Plusmang lại tốc độ đọc/ghi lên đến 3500/3300 MB/s, nhanh hơn 53% so với phiên bản 970 EVO. Sử dụng công nghệ V-NAND mang đến hiệu suất tốt hơn cũng như giảm lượng điện năng tiêu thụ đi kèm đó là vi điều khiển Phoenix, phần mềm tăng tốc thông minh TurboWrite với dung lượng 250GB thực hiện những công việc đồ họa chuyên sâu một cách tốt nhất.Ổ cứngSSD Samsung 970 EVO Plus 250GB&nbsp;M2 2280với việc sử dụng công nghệ PCIe NVMe mới nhất, đi kèm với thiết kế nhỏ gọn giúp mở rộng tối đa không gian lưu trữ dữ liệu cũng như không gian bên trong của máy tính. Công nghệ mới nhất của Samsung cho phép người dùng có thêm sức chứa để làm nhiều việc hơn, trải nghiệm tốt hơn.Bảo vệ&nbsp;dữ liệu của bạn an toàn qua nhiều năm sử dụngVới nhu cầu đảm bảo an toàn dữ liệu mà không làm giảm hiệu suất của ổ cứng.SSD Samsung 970 EVO Plusvới độ bền lâu dài nhờ vào công nghệ V-NAND mới nhất, cũng như công nghệ của Samsung có thể sử dụng được 150 TBW hoặc đến 5 năm sử dụng mà không hề suy giảm hiệu năng.Công nghệ Dynamic Thermal GuardSSD Sansung 970 EVO Plus 250GBsở hữu công nghệ Dynamic Thermal Guard, công nghệ tự động điều chỉnh nhiệt độ, khi nhiệt độ ổ cứng vượt quá mức cho phép hoặc nếu như nó lên đến mức nguy hiểm thì thiết bị sẽ tự động điều chỉnh hiệu năng để không làm hư hao dữ liệu.&nbsp; Kèm theo một giải pháp tản nhiệt mới của Samsung đó là bọc Niken cho vi điều khiển.Trình quản lý&nbsp;Samsung MagicianViệc sử dụng ổ cứng sẽ ngày càng đơn giản hơn khi mà có sự giúp sức của Samsung Magician. Phần mềm này sẽ giúp người dùng có thể theo dõi được thông số ổ cứng đang sử dụng. Với giao diện thân thiện, dễ sử dụng Samsung Magician cho phép người dùng tự động update driver, quản lý hiệu năng, độ bền của SSD và cả tăng tốc độ truyền dữ liệu lên.',
        1, 'https://product.hstatic.net/200000722513/product/70-evo-plus-250gb-ssd-m.2-nvme-gearvn_ebf82e635e2e4ec685b5b1401bdcd2e3_46e1f62edef94e69a007175b0ef1fe38.jpg'
    );
