INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím AKKO 3098B Plus Black & Gold Fairy (Silent) switch', 'ban-phim-akko-3098b-plus-black-gold-fairy-silent-switch', 4,
        '{"Bảo hành": "12 tháng", "Cấu Trúc": "Tray Mount", "Vỏ + Phím": "PBT&nbsp;Keycaps + ABS Frame", "Kết Nối": "Bluetooth5.0 / 2.4GHz / USB Type-C", "LED": "South-facing RGB", "Switches": "Akko Fairy (Silent)", "Hot Swappable": "5-Pin Support", "N-Key Rollover": "Yes", "Keycaps": "Cherry Profile", "Trình điều khiển": "Akko Macro V1.0", "Pin": "3000mAh", "Kích Thước": "382*134*40mm", "Trọng lượng": "1.2Kg", "Phụ Kiện Bàn phím": "&nbsp;HDSD, Cáp, Keycaps tặng,Keycap Puller, Switch Puller, Nắp chống bụi"}',
        'Nâng tầm trải nghiệm gõ phím của bạn với Bàn phím cơ AKKO 3098B Plus Black & Gold Fairy (Silent) switch – sự kết hợp hoàn hảo giữa vẻ đẹp sang trọng, hiệu năng vượt trội và âm thanh êm ái. Khoác lên mình tông màu Đen huyền bí điểm xuyết những chi tiết Vàng đồng tinh tế, chiếc bàn phím này không chỉ là một công cụ làm việc mà còn là một món đồ trang sức cao cấp cho không gian của bạn.\n\nĐiểm nhấn đặc biệt của phiên bản này chính là các switch ''Silent'' cao cấp, được tinh chỉnh để mang lại trải nghiệm gõ phím cực kỳ êm ái và mượt mà. Giờ đây, bạn có thể làm việc, học tập hay chơi game mà không lo làm phiền những người xung quanh, lý tưởng cho môi trường văn phòng, thư viện hoặc sử dụng vào ban đêm.\n\nAKKO 3098B Plus được trang bị layout 98 phím tối ưu, giữ lại đầy đủ các phím chức năng cần thiết trong một kích thước gọn gàng. Keycap PBT Doubleshot chất lượng cao không chỉ bền bỉ, chống mờ theo thời gian mà còn mang lại cảm giác gõ chắc chắn và cao cấp. Khả năng hot-swap tiện lợi cho phép bạn dễ dàng thay đổi switch yêu thích mà không cần hàn, mang đến sự tùy biến không giới hạn. Hỗ trợ đa kết nối (USB Type-C, Bluetooth 5.0, 2.4G Wireless) đảm bảo sự linh hoạt tối đa, tương thích với mọi thiết bị. Hệ thống đèn nền RGB rực rỡ với nhiều chế độ tùy chỉnh sẽ thổi hồn vào không gian làm việc của bạn, tạo nên một trải nghiệm thị giác ấn tượng.\n\nAKKO 3098B Plus Black & Gold Fairy Silent không chỉ là một chiếc bàn phím, mà là một tuyên ngôn về phong cách và hiệu suất, dành cho những ai tìm kiếm sự hoàn hảo trong từng cú gõ.',
        1, 'https://cdn.hstatic.net/products/200000722513/imgi_47_3098_cherry__________rgb_1_a39b1dc9443b481c975ee2d73e88ab3b.png'
    );
