INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím AKKO có dây TAC75 White HE Astrolink Magnetic Switch', 'ban-phim-akko-co-day-tac75-white-he-astrolink-magnetic-switch', 4,
        '{"Bảo hành": "12 tháng", "Cấu trúc": "Tray Mount", "Vỏ + Phím": "PBT Double-Shot", "Màu sắc": "Trắng", "Kết nối": "USB Type-C", "Đèn LED": "RGB", "Switches": "Akko Astrolink Magnetic Switch", "Polling Rate": "8,000Hz", "Hot Swappable": "Không hỗ trợ (N/A)", "N-Key Rollover": "Có", "Trình điều khiển": "Akko Cloud Driver", "In hông (Side-Printed)": "Có", "Kích thước": "333 x 141 x 33 mm", "Trọng lượng": "0.7 kg"}',
        'Chinh phục mọi thử thách với Bàn phím AKKO có dây TAC75 White HE Astrolink Magnetic Switch – biểu tượng của sự kết hợp hoàn hảo giữa hiệu năng đỉnh cao, công nghệ tiên tiến và vẻ đẹp tinh tế. Được thiết kế dành cho game thủ và những người đam mê gõ phím yêu cầu sự chính xác tuyệt đối, TAC75 White HE sẽ đưa trải nghiệm của bạn lên một tầm cao mới. \n\nĐiểm nhấn cốt lõi của chiếc bàn phím này chính là hệ thống công tắc từ tính (Magnetic Switch) Astrolink HE độc quyền. Công nghệ đột phá này mang lại khả năng Rapid Trigger vượt trội, cho phép phím được kích hoạt và reset cực nhanh chỉ với một khoảng di chuyển siêu nhỏ, đồng nghĩa với phản hồi tức thì trong từng pha hành động, từ đó tạo lợi thế quyết định trong các tựa game FPS hay nhịp độ nhanh. Không chỉ vậy, bạn còn có thể tùy chỉnh điểm kích hoạt (Actuation Point) của từng phím theo ý muốn, từ độ nhạy cảm ứng chỉ 0.1mm cho đến độ sâu chắc chắn hơn, mang lại khả năng cá nhân hóa chưa từng có. Với cơ chế không tiếp xúc vật lý, công tắc từ tính còn đảm bảo độ bền bỉ vượt trội và tuổi thọ lâu dài.\n\nVề thiết kế, AKKO TAC75 White HE sở hữu layout 75% nhỏ gọn nhưng vẫn giữ lại đầy đủ hàng phím chức năng (F-row) và cụm phím điều hướng quan trọng, tối ưu hóa không gian làm việc mà không làm giảm tính tiện dụng. Sắc trắng tinh khôi không chỉ mang lại vẻ đẹp thanh lịch, sang trọng mà còn dễ dàng hòa nhập vào mọi góc setup, tạo nên một tổng thể hài hòa và hiện đại. Kết nối có dây ổn định, đảm bảo đường truyền tín hiệu không gián đoạn và độ trễ cực thấp, là yếu tố then chốt cho mọi trận đấu căng thẳng hay những phiên làm việc cần sự tập trung cao độ.\n\nAKKO TAC75 White HE Astrolink Magnetic Switch không chỉ là một chiếc bàn phím, mà là một công cụ mạnh mẽ giúp bạn khai phá tối đa tiềm năng, biến mỗi cú nhấn thành một hành động chính xác và hiệu quả.',
        1, 'https://cdn.hstatic.net/products/200000722513/imgi_45_46_6969e9527b2e4f1ba885de6c508e35a5.png'
    );
