INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím ASUS M605 ROG FALCHION ACE HFX', 'ban-phim-asus-m605-rog-falchion-ace-hfx', 3,
        '{"Loại bàn phím": "Bàn phím cơ", "Layout bàn phím": "65%", "Số phím bấm": "68 phím", "Chuẩn kết nối": "2x Wired USB-C", "Chất liệu bàn phím": "Khung phím Nhựa", "Switch bàn phím": "ROG HFX Magnetic Switches", "Keycaps bàn phím": "PBT Doubleshot", "Đèn nền": "LED RGB", "N-Key Rollover": "Có", "Polling Rate": "USB Report&nbsp;Lên đến 8000Hz", "Onboard Memory": "Có", "Tính năng bàn phím": "Rapid Trigger, Speed Tap, Gasket Mount, Foam Tiêu âm, Stabilizer", "Loại pin": "Pin", "Phụ kiện": "1x Cáp USB-A to C, 1x ROG Keycap Puller, 1x Phím Ctrl", "Phần mềm": "Armoury Crate", "Kích thước": "315 x 115 x 35 mm", "Trọng lượng": "643g", "Màu sắc": "Black", "Thời gian bảo hành": "24 tháng"}',
        'Nâng tầm trải nghiệm chơi game và gõ phím của bạn với Bàn phím ASUS M605 ROG FALCHION ACE HFX – kiệt tác công nghệ thu gọn trong một thiết kế 65% cực kỳ di động, nhưng ẩn chứa sức mạnh vượt trội. Được chế tác dành cho game thủ chuyên nghiệp và những ai yêu thích sự tối giản mà vẫn đòi hỏi hiệu suất đỉnh cao, Falchion Ace HFX mang đến sự kết hợp hoàn hảo giữa độ chính xác, tốc độ và tiện lợi.\n\nVới công nghệ kết nối không dây ROG SpeedNova™ siêu nhanh ở tần số 2.4 GHz, bạn sẽ tận hưởng trải nghiệm không độ trễ, như thể đang dùng bàn phím có dây, nhưng không bị vướng víu. Cảm giác gõ phím được nâng tầm nhờ các switch cơ học ROG NX độc quyền, có thể thay nóng (hot-swappable) mà không cần hàn, cho phép bạn tùy chỉnh cảm giác gõ theo ý muốn. Khung phím chắc chắn, keycaps PBT doubleshot bền bỉ đảm bảo tuổi thọ lâu dài và cảm giác chạm cao cấp.\n\nThiết kế nhỏ gọn 65% không chỉ giúp tiết kiệm không gian trên bàn làm việc mà còn tối ưu hóa tính di động, dễ dàng mang theo đến mọi giải đấu hay môi trường làm việc. Đừng quên dải đèn RGB Aura Sync rực rỡ, cho phép cá nhân hóa không gian chơi game của bạn với hàng triệu màu sắc và hiệu ứng ánh sáng đồng bộ. ASUS M605 ROG FALCHION ACE HFX không chỉ là một chiếc bàn phím, mà là một tuyên ngôn về phong cách và hiệu suất.',
        1, 'https://cdn.hstatic.net/products/200000722513/imgi_230_ban-phim-co-asus-m605-rog-falchion-ace-rog-hfx-sw-12_a4b9c7c4162d45c5a8af32a2ae3098dc.jpg'
    );
