INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím E-Dra EK375W (White + Black + Gray)', 'ban-phim-e-dra-ek375w-ek398w-white-black-gray', 23,
        '{"- Bảo hành": "24 tháng", "- Giao diện": "3 chế độ kết nối (2.4Ghz, Bluetooth, Dây)", "- Số phím": "97 phím", "- Khoảng cách hoạt động": "10m", "- Cáp bàn phím": "Dài 1.6m", "- Keycaps": "PBT", "- Antighosting": "full antishosting keys", "- Tương thích hệ điều hành": "Windows 98 / 2000 / ME / NT / XP / win 7,8,10", "- Màu sắc": "Case đen, keycap Đen-Trắng-Xanh", "- Switch": "Blueberry linear", "- Kích thước": "394*145*43mm", "- Cân nặng": "795+10g"}',
        'Bàn phím cơ E-Dra EK375W - Tuyệt tác hòa quyện giữa phong cách và hiệu năng. Với sự kết hợp tinh tế của ba tông màu trắng, đen và xám, EK375W không chỉ là một công cụ gõ phím mà còn là một điểm nhấn ấn tượng, nâng tầm thẩm mỹ cho không gian làm việc hay giải trí của bạn. Vẻ ngoài thanh lịch, hiện đại được chế tác tỉ mỉ, mang đến cảm giác cao cấp và tinh tế ngay từ cái nhìn đầu tiên.\n\nKhông chỉ dừng lại ở vẻ đẹp, E-Dra EK375W còn chinh phục người dùng bằng hiệu năng vượt trội và trải nghiệm gõ phím đỉnh cao. Được trang bị các switch cơ học chất lượng cao, mỗi lần nhấn phím đều mang lại phản hồi xúc giác rõ ràng và âm thanh êm ái, lý tưởng cho cả công việc nhập liệu tốc độ cao lẫn những trận game gay cấn. Công nghệ chống ghosting toàn bộ phím (N-key rollover) đảm bảo mọi lệnh đều được ghi nhận chính xác, không bỏ lỡ bất kỳ khoảnh khắc nào dù bạn thao tác nhanh đến đâu.\n\nTrải nghiệm người dùng được nâng tầm với khả năng kết nối đa dạng và linh hoạt. Bàn phím hỗ trợ cả kết nối không dây 2.4GHz tốc độ cao với độ trễ thấp, Bluetooth tiện lợi cho việc kết nối nhiều thiết bị cùng lúc và kết nối có dây USB-C ổn định, giúp bạn dễ dàng chuyển đổi giữa các chế độ tùy theo nhu cầu sử dụng. Keycap PBT cao cấp, chống bóng mờ và bền bỉ theo thời gian, mang lại cảm giác gõ chắc chắn, thoải mái và duy trì vẻ đẹp nguyên bản. Đèn nền LED tùy chỉnh với nhiều hiệu ứng động càng làm tăng thêm vẻ cuốn hút, tạo không gian làm việc đầy cảm hứng và cá tính.\n\nE-Dra EK375W (White + Black + Gray) không chỉ là một bàn phím, mà là một tuyên ngôn về phong cách và đẳng cấp, mang đến sự kết hợp hoàn hảo giữa thẩm mỹ, độ bền và hiệu suất, hứa hẹn sẽ làm hài lòng mọi người dùng khó tính nhất.',
        1, 'https://cdn.hstatic.net/products/200000722513/imgi_11_595_ek398w_black_white_gray_1__d0e11da1d5174027b13f243542cf29e6.jpg'
    );
