INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím E-Dra EK375W White', 'ban-phim-e-dra-ek375w-white', 23,
        '{"- Bảo hành": "24 tháng", "- Giao diện": "USB + Bluetooth + 2.4Ghz- Cấu trúc Hotswap- Số lượng phím: 82 phím- Cáp bàn phím: Type-C dài 1.5m- Keycaps: PBT Doubleshot- Led RGB- Antighosting: full antishosting keys- Màu sắc: Case đen, keycap Đen-Xanh- Switch: EDRA Linear Custom Switch- Pin: 4000mAh- Kích thước: 335*145*44mm- Trọng lượng: 716g- Tương thích hệ điều hành: Windows 98 / 2000 / ME / NT / XP / win 7,8,10"}',
        'Nâng tầm không gian làm việc và giải trí của bạn với Bàn phím cơ không dây E-Dra EK375W White – sự hòa quyện tinh tế giữa thiết kế hiện đại, hiệu suất vượt trội và sự tiện lợi không giới hạn. Khoác lên mình sắc trắng thuần khiết, EK375W không chỉ là một công cụ, mà còn là một tuyên ngôn phong cách, dễ dàng làm bừng sáng mọi góc độ từ bàn làm việc tối giản đến trạm chiến đấu gaming đỉnh cao.\n\nTrải nghiệm sự tự do tuyệt đối với kết nối không dây đa chế độ: công nghệ 2.4GHz siêu tốc đảm bảo độ trễ gần như bằng 0 cho những pha gaming quyết định, trong khi Bluetooth tiện lợi cho phép ghép nối nhanh chóng với nhiều thiết bị. Không còn dây cáp rườm rà, không gian của bạn sẽ trở nên gọn gàng và thoáng đãng hơn bao giờ hết.\n\nDưới lớp vỏ thanh lịch là trái tim cơ học mạnh mẽ. E-Dra EK375W White mang đến cảm giác gõ phím tuyệt vời với các switch cơ chất lượng cao, phản hồi nhanh nhạy và độ bền vượt trội. Đặc biệt, tính năng Hot-Swappable 5 pin cho phép bạn dễ dàng thay đổi loại switch mà không cần hàn, mở ra cánh cửa cá nhân hóa vô tận để tìm kiếm cảm giác gõ hoàn hảo của riêng mình. Mỗi phím nhấn là một trải nghiệm êm ái, chính xác, lý tưởng cho cả việc soạn thảo văn bản dài hay những trận game gay cấn.\n\nKhông chỉ vậy, hệ thống đèn nền RGB rực rỡ với nhiều hiệu ứng ánh sáng động sẽ biến bàn phím của bạn thành một bữa tiệc thị giác đầy màu sắc, có thể tùy chỉnh để phù hợp với tâm trạng hoặc phong cách cá nhân. Với pin dung lượng lớn đảm bảo thời gian sử dụng lâu dài và khả năng tương thích rộng rãi, Bàn phím cơ không dây E-Dra EK375W White chính là lựa chọn hoàn hảo cho những ai tìm kiếm một thiết bị đẳng cấp, bền bỉ, linh hoạt và đầy cá tính.',
        1, 'https://cdn.hstatic.net/products/200000722513/imgi_19_572_ek375w_2-photoroom_d6bd5548284b4ddf896b578316264d43.png'
    );
