INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím E-Dra EK375W Black', 'ban-phim-e-dra-ek375w-black', 23,
        '{"- Bảo hành": "24 tháng", "- Giao diện": "USB + Bluetooth + 2.4Ghz- Cấu trúc Hotswap- Số lượng phím: 82 phím- Cáp bàn phím: Type-C dài 1.5m- Keycaps: PBT Doubleshot- Led RGB- Antighosting: full antishosting keys- Màu sắc: Case đen, keycap Đen-Xanh- Switch: EDRA Linear Custom Switch- Pin: 4000mAh- Kích thước: 335*145*44mm- Trọng lượng: 716g- Tương thích hệ điều hành: Windows 98 / 2000 / ME / NT / XP / win 7,8,10"}',
        'Bàn phím cơ E-Dra EK375W Black là sự kết hợp hoàn hảo giữa thiết kế tinh tế, hiệu năng mạnh mẽ và tính linh hoạt vượt trội, mang đến trải nghiệm gõ phím đẳng cấp cho mọi đối tượng người dùng. Với layout 75% nhỏ gọn, chiếc bàn phím này không chỉ tối ưu không gian làm việc mà còn dễ dàng mang theo, trở thành người bạn đồng hành lý tưởng cho cả công việc và giải trí.\n\nKhoác lên mình tông màu đen tuyền lịch lãm, EK375W Black toát lên vẻ chuyên nghiệp và hiện đại, dễ dàng hòa nhập với mọi thiết lập gaming hay văn phòng. Trái tim của bàn phím là các switch cơ học chất lượng cao, mang lại cảm giác gõ đầm tay, phản hồi xúc giác rõ ràng và âm thanh gõ đặc trưng mà dân mê phím cơ yêu thích. Dù bạn là game thủ cần tốc độ phản hồi nhanh nhạy hay nhân viên văn phòng cần sự thoải mái khi gõ lâu, EK375W đều đáp ứng xuất sắc.\n\nĐiểm nhấn đặc biệt của E-Dra EK375W Black nằm ở khả năng kết nối đa dạng và tiện lợi. Bạn có thể dễ dàng chuyển đổi giữa ba chế độ: không dây 2.4GHz siêu tốc với độ trễ cực thấp qua USB Receiver, kết nối Bluetooth ổn định và linh hoạt với nhiều thiết bị (máy tính bảng, điện thoại, laptop) hoặc kết nối có dây USB Type-C đáng tin cậy, đảm bảo không bao giờ bị gián đoạn.\n\nKeycap được làm từ chất liệu bền bỉ, chống mài mòn, đảm bảo độ bền cao và giữ cho các ký tự luôn rõ nét theo thời gian. Hệ thống đèn nền sống động không chỉ tạo điểm nhấn thẩm mỹ mà còn hỗ trợ người dùng thao tác dễ dàng trong điều kiện thiếu sáng.\n\nVới E-Dra EK375W Black, bạn không chỉ sở hữu một công cụ gõ phím mà còn là một trợ thủ đắc lực, nâng cao hiệu suất làm việc và thăng hoa cảm xúc trong mọi trận game.',
        1, 'https://cdn.hstatic.net/products/200000722513/imgi_11_571_ek375w_11_3fcee21462914d6296858d427f27d0be.jpg'
    );
