INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím AKKO 5098B The King’s Avatar Ye Xiu V3 Piano Pro switch', 'ban-phim-akko-5098b-the-king-s-avatar-ye-xiu-v3-piano-pro-switch', 4,
        '{"Màu sắc và họa tiết": "Sự kết hợp giữa đen và vàng gold tạo nên vẻ ngoài sang trọng và mạnh mẽ. Các họa tiết được in trên keycap và vỏ bàn phím tái hiện lại những biểu tượng và hình ảnh quen thuộc trong \"Toàn Chức Cao Thủ\", mang đến sự độc đáo và cá tính cho sản phẩm.", "Layout 98%": "Thiết kế này giúp bàn phím nhỏ gọn hơn so với full-size, phù hợp với những người dùng có không gian bàn làm việc hạn chế hoặc thường xuyên di chuyển. Tuy nhiên, nó vẫn giữ lại đầy đủ các phím số và phím chức năng cần thiết.", "Chất liệu PBT": "PBT (Polybutylene Terephthalate) là một loại nhựa cao cấp, có độ bền cao, chống mài mòn và không bị bóng sau thời gian dài sử dụng. Điều này đảm bảo keycap luôn giữ được vẻ đẹp ban đầu.", "Công nghệ in Dye-subbed": "Công nghệ in nhiệt thăng hoa (Dye-sublimation) giúp mực in thấm sâu vào keycap, tạo ra những ký tự sắc nét, bền màu và không bị phai theo thời gian.", "Profile Cherry": "Profile Cherry có độ cao vừa phải, bề mặt keycap lõm nhẹ, ôm ngón tay, mang lại cảm giác gõ phím thoải mái và chính xác.", "Bluetooth 5.0": "Kết nối không dây ổn định với nhiều thiết bị, phù hợp cho việc sử dụng hàng ngày.", "Wireless 2.4GHz": "Kết nối không dây với độ trễ thấp, phù hợp cho việc chơi game.", "USB Type-C": "Kết nối có dây với tốc độ truyền tải dữ liệu nhanh chóng và ổn định."}',
        'AKKO 5098B The King’s Avatar Ye Xiu là một chiếcbàn phím cơđộc đáo, lấy cảm hứng từ bộ truyện và phim hoạt hình nổi tiếng \"Toàn Chức Cao Thủ\" (The King’s Avatar). Với thiết kế bắt mắt, hiệu năng gõ phím tuyệt vời và nhiều tính năng hiện đại, sản phẩm này hứa hẹn sẽ làm hài lòng cả những game thủ và người dùng khó tính nhất. Cùng GEARVN đánh giá chi tiết sản phẩmbàn phímnày trong phần dưới đây!',
        1, 'https://product.hstatic.net/200000722513/product/trai_c86aaec9b6ed4d099d2a9883ceefa27b.png'
    );
