INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Keychron B6P-K2 White Scissor', 'ban-phim-keychron-b6p-k2-white-scissor', 10,
        '{"Layout": "Fullsize", "Số lượng nút phím": "109 phím", "Chất liệu vỏ": "Nhựa", "Màu sắc": "Xám / Đen", "Keycap": "ABS", "Switch": "Scissor", "LED": "-", "Kết nối": "Wireless 2.4GHz / Bluetooth / Dây USB", "Loại dây": "Dây USB-C to A", "Kích thước (mm)": "429 x 130 x 14.5 mm", "Dung lượng - Thời lượng pin": "800mAh", "Polling Rate": "Lên đến 1000Hz"}',
        'Trải nghiệm đỉnh cao của sự tinh tế và hiệu suất với Bàn phím Keychron B6P-K2 White Scissor. Được thiết kế dành riêng cho những ai đề cao sự gọn gàng, yên tĩnh và thoải mái, chiếc bàn phím này không chỉ là một công cụ làm việc mà còn là một điểm nhấn thanh lịch trên mọi không gian. Với sắc trắng thuần khiết, B6P-K2 mang đến vẻ đẹp tối giản, hiện đại, dễ dàng hòa nhập vào môi trường làm việc hay góc học tập của bạn.\n\nĐiểm nổi bật của B6P-K2 chính là công nghệ switch cắt kéo (scissor switch) cao cấp, mang lại hành trình phím ngắn, độ phản hồi cực kỳ nhạy bén và cảm giác gõ êm ái, yên tĩnh đến bất ngờ. Đây là lựa chọn lý tưởng cho những buổi làm việc dài, các cuộc họp trực tuyến hay bất cứ khi nào bạn cần sự tập trung tuyệt đối mà không bị làm phiền bởi tiếng ồn. \n\nVới độ mỏng ấn tượng và khả năng kết nối không dây Bluetooth ổn định, B6P-K2 đảm bảo tính di động tối đa, cho phép bạn dễ dàng mang theo và làm việc hiệu quả ở bất cứ đâu. Hỗ trợ đa hệ điều hành từ macOS, Windows đến iOS và Android, chiếc bàn phím này là người bạn đồng hành lý tưởng cho mọi thiết bị. Keychron B6P-K2 White Scissor là sự kết hợp hoàn hảo giữa phong cách, sự tiện lợi và trải nghiệm gõ phím vượt trội, định nghĩa lại chuẩn mực của một bàn phím văn phòng hiện đại.',
        1, 'https://product.hstatic.net/200000722513/product/__9d5671d40b3b47259753c097ade033d7.png'
    );
