INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Keychron V3M-D3 Carbon Black Brown Switch', 'ban-phim-keychron-v3m-d3-carbon-black-brown-switch', 10,
        '{"Kích thước": "367.8 x 149 x 32.5 mm", "Phân loại": "Bàn phím cơ không dây", "Cổng kết nối": "Wireless 2.4 GHz / Bluetooth 5.1 / Type-C", "Hệ điều hành hỗ trợ": "MacOS / Windows / Linux", "Màu sắc": "Đen", "Khối lượng": "895 ± 10 g", "Chiều dài dây": "1.8m", "Bảo hành": "12 tháng", "Chất liệu": "Nhựa ABS + PC", "Dung lượng Pin": "4000 mAh", "Số lượng phím": "88", "Kiểu switch": "Brown Switch", "Đèn bàn phím": "LED RGB"}',
        'Đắm mình vào trải nghiệm gõ phím đỉnh cao với Bàn phím Keychron V3M-D3 Carbon Black Brown Switch – sự kết hợp hoàn hảo giữa hiệu suất vượt trội, khả năng tùy chỉnh không giới hạn và phong cách tinh tế. Được thiết kế dành cho những người dùng khó tính nhất, V3M-D3 tự hào với nền tảng cơ khí vững chắc và hỗ trợ QMK/VIA đầy đủ, cho phép bạn lập trình lại mọi phím, tạo macro phức tạp và điều chỉnh hiệu ứng ánh sáng RGB rực rỡ theo ý muốn, biến chiếc bàn phím này thành công cụ làm việc và giải trí thực sự của riêng bạn. Phiên bản Carbon Black khoác lên mình vẻ ngoài sang trọng, mạnh mẽ và chuyên nghiệp, dễ dàng hòa nhập vào bất kỳ không gian làm việc hay setup gaming nào, đồng thời toát lên sự đẳng cấp và bền bỉ. Trung tâm của trải nghiệm gõ phím tuyệt vời này là các Brown Switch xúc giác (tactile) trứ danh. Với lực nhấn vừa phải và một cú nhấp nhẹ nhàng, tinh tế khi kích hoạt, Brown Switch mang lại phản hồi xúc giác rõ ràng mà không quá ồn ào, lý tưởng cho những phiên làm việc dài cần sự tập trung cao độ, hay những trận gaming cần độ chính xác mà vẫn giữ được sự yên tĩnh. Đây là lựa chọn hoàn hảo cho những ai tìm kiếm sự cân bằng giữa tốc độ, độ chính xác và cảm giác gõ êm ái, dễ chịu. Keychron V3M-D3 Carbon Black Brown Switch không chỉ là một chiếc bàn phím, mà là một khoản đầu tư vào năng suất và niềm đam mê. Nó đại diện cho sự tự do tùy chỉnh, chất lượng bền bỉ và một thiết kế không lỗi thời, sẵn sàng nâng tầm trải nghiệm gõ phím của bạn lên một tầm cao mới. Hãy sẵn sàng để khám phá sự khác biệt!',
        1, 'https://product.hstatic.net/200000722513/product/download__2__20de7c57205b42a682a6b25fe55322bf.png'
    );
