INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Keychron Q1H-P1 White Magnetic Switch', 'ban-phim-keychron-q1h-p1-white-magnetic-switch', 10,
        '{"Loại bàn phím": "Bàn phím cơ không dây", "Layout bàn phím": "75%", "Số phím bấm": "82 phím", "Chuẩn kết nối": "Detachable Wired USB-C / Bluetooth / Wireless 2.4GHz", "Chất liệu bàn phím": "Khung: Nhôm CNC Plate: Nhôm", "Keycaps": "PBT Doubleshot, OSA Profile", "Đèn nền": "LED RGB", "N-Key Rollover": "Có", "Onboard Memory": "Không rõ", "Loại pin": "Pin sạc", "Dung lượng pin": "4000 mAh", "Thời lượng pin": "Có đèn nền: Lên đến 100 giờ", "Phần mềm": "Keychron Launcher", "Kích thước": "327.5 x 145 mm", "Trọng lượng": "1735 ± 10 g", "Màu sắc": "Shell White", "Hệ điều hành hỗ trợ": "Windows 10/11, MacOS, Linux, iOS/iPadOS, Android"}',
        'Nâng tầm trải nghiệm của bạn với Bàn phím Keychron Q1H-P1 White Magnetic Switch – tuyệt tác kết hợp giữa công nghệ tiên tiến và thiết kế tinh xảo. Được chế tạo đặc biệt cho những game thủ và người đam mê gõ phím đòi hỏi sự hoàn hảo, Q1H-P1 không chỉ là một bàn phím mà còn là một công cụ giúp bạn chinh phục mọi thử thách.\n\nĐiểm nhấn làm nên sự khác biệt của Q1H-P1 chính là công tắc từ tính (Magnetic Switch) màu trắng độc quyền. Với công nghệ Rapid Trigger siêu nhạy, mỗi lần nhấn phím đều được ghi nhận ngay lập tức, cho phép bạn thực hiện các thao tác lặp lại với tốc độ chóng mặt. Đặc biệt, bạn có thể dễ dàng điều chỉnh điểm kích hoạt (Actuation Point) của từng phím theo từng milimet, tùy biến độ nhạy để phù hợp tuyệt đối với phong cách chơi game tốc độ cao hoặc gõ phím chính xác của bạn.\n\nKeychron Q1H-P1 kế thừa trọn vẹn sự cao cấp từ dòng Q series với khung nhôm nguyên khối chắc chắn, lớp hoàn thiện màu trắng trang nhã, tạo nên vẻ đẹp vượt thời gian. Cấu trúc Gasket Mount danh tiếng không chỉ mang lại cảm giác gõ êm ái, đàn hồi mà còn giảm thiểu tiếng ồn, biến mỗi lần chạm phím thành một trải nghiệm thoải mái và dễ chịu.\n\nVới khả năng tương thích QMK/VIA mạnh mẽ, mọi phím bấm đều nằm trong tầm kiểm soát của bạn. Tùy chỉnh macro, gán lại chức năng, hoặc thiết lập hiệu ứng đèn nền RGB rực rỡ – tất cả đều được thực hiện một cách dễ dàng. Trải nghiệm gõ phím mượt mà, độ trễ cực thấp và khả năng tùy biến vô hạn biến Keychron Q1H-P1 trở thành lựa chọn hàng đầu cho những ai tìm kiếm hiệu suất vượt trội và sự độc đáo. Hãy sẵn sàng cho một kỷ nguyên mới của sự chính xác và tốc độ!',
        1, 'https://product.hstatic.net/200000722513/product/download__11__0bdd4e376f49442299eaf0a28303022b.png'
    );
