INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Keychron K2H-F1 Black Magnetic Switch', 'ban-phim-keychron-k2h-f1-black-magnetic-switch', 10,
        '{"Thiết kế": "Bố cục 75%", "Kích thước": "Dài: 320.5 mm - Rộng: 126.7 mm", "Trọng lượng": "~965g", "Chất liệu khung": "Nhôm + Gỗ", "Chất liệu vỏ đáy": "Nhựa ABS", "Keycaps": "PBT hai lớp OSA, không bóng", "Kết nối": "Wireless 2.4GHz / Bluetooth 5.2 / Wired Type-C", "Cáp đi kèm": "Type-A to Type-C", "Pin": "Li-polymer 4000 mAh (sạc được)", "Thời lượng pin": "Lên đến 110 giờ (không dây, tắt đèn nền)", "N-Key Rollover": "Có (cả không dây và có dây)", "Action Keys": "Có, 4-in-1", "Hot-swappable": "Có", "Tần số phản hồi (Polling)": "1000Hz (có dây và 2.4GHz) 125Hz (Bluetooth)"}',
        'Keychron K2 HE Special Edition RGB Hotswap Aluminum (Magnetic Sw) là chiếc bàn phím không dây 75% đầu tiên sử dụng switch từ tính Gateron Double-Rail Magnetic, cho phép người dùng tùy chỉnh điểm nhận phím từ 0.2mm đến 3.8mm. Công nghệ Rapid Trigger và Dynamic Keystroke (DKS) giúp thực thi nhiều thao tác trên một phím theo độ sâu nhấn – tối ưu hóa tốc độ và độ phản hồi trong các tựa game FPS hoặc thao tác chuyên sâu.\\n\\n **Tích hợp Analog Mode – Biến bàn phím thành tay cầm chơi game chính xác**\\nAnalog Mode trên Keychron K2 HE mô phỏng cách hoạt động của tay cầm chơi game, nhận diện lực nhấn để điều chỉnh tốc độ và cường độ trong game, giúp người dùng điều hướng nhân vật mượt mà hơn trong các trò chơi đua xe, thể thao hoặc hành động mô phỏng. Đây là tính năng rất hiếm xuất hiện trên các bàn phím không dây truyền thống.\\n\\n**Thiết kế sang trọng – Khung nhôm viền gỗ, switch hot-swap, keycap PBT cao cấp**\\nKeychron K2 HE Special Edition có khung nhôm nguyên khối kết hợp viền gỗ tự nhiên, cho cảm giác chắc chắn và đẳng cấp. Bộ keycap Double-shot PBT profile OSA, chống bóng dầu, bền màu và tăng cảm giác gõ. Bàn phím hỗ trợ hot-swap với switch từ tính (Hall Effect), dễ dàng thay đổi switch theo sở thích mà không cần hàn lại mạch.\\n\\n**Kết nối 3 chế độ – Bluetooth 5.2, 2.4GHz và USB-C với polling rate 1000Hz**\\nHỗ trợ Bluetooth 5.2, 2.4GHz không dây (1000Hz) và USB-C có dây, K2 HE cho khả năng kết nối cực nhanh, chuyển đổi mượt giữa tối đa 3 thiết bị. Tương thích tốt với Mac, Windows, và đi kèm keycap riêng cho từng hệ điều hành, đồng thời có thể lưu 2 layout tùy chỉnh cho mỗi nền tảng.\\n![Ảnh](https://bizweb.dktcdn.net/100/329/122/files/ban-phim-co-khong-day-keychron-k2-he-special-edition-rgb-hotswap-aluminum-magnetic-sw-04.jpg?v=1748257090613)\\n\\n**Cá nhân hóa toàn diện với Launcher Web Configurator & QMK/VIA**\\nBàn phím hỗ trợ phần mềm Keychron Launcher (Web Configurator) và firmware QMK/VIA, giúp người dùng tùy biến từng phím, macro, đèn RGB, và điểm nhận phím theo ý muốn. Ngoài ra, bàn phím tích hợp đèn RGB north-facing với hơn 22 hiệu ứng ánh sáng tùy chỉnh trong phần mềm mà không cần cài thêm driver.\\n\\n**Cấu trúc âm học cải tiến – Gõ êm, \\\"thock\\\" nhẹ, trải nghiệm đỉnh cao**\\nBên trong Keychron K2 HE là hệ thống tiêu âm gồm EVA foam, EPDM foam, silicone acoustic pad, cùng switch đã được pre-lubed sẵn. Thiết kế này tạo nên tiếng gõ êm ái, “thocky” và mềm mại, mang đến trải nghiệm gõ thoải mái suốt nhiều giờ liền. Pin 4000mAh cho thời lượng sử dụng đến 110 giờ khi tắt đèn nền.\\n![Ảnh](https://bizweb.dktcdn.net/100/329/122/files/ban-phim-co-khong-day-keychron-k2-he-special-edition-rgb-hotswap-aluminum-magnetic-sw-06.jpg?v=1748257090707)',
        1, 'https://product.hstatic.net/200000722513/product/download__10__61c98425dc764440b74a393e8ff23ee2.png'
    );
