INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím AULA F75 (Đen +Hồng Gradient/Reaper switch) F7514', 'ban-phim-aula-f75-den-hong-gradient-reaper-switch', 22,
        '{}',
        'Khám phá Bàn phím AULA F75 F7514 – Nơi công nghệ và phong cách hòa quyện một cách ngoạn mục. Với sự kết hợp màu sắc Đen và Hồng Gradient độc đáo, chiếc bàn phím này không chỉ là một công cụ gõ phím mà còn là một tác phẩm nghệ thuật làm bừng sáng không gian làm việc hay giải trí của bạn. Trải nghiệm đỉnh cao cùng Switch Reaper độc quyền. Từng cú nhấn phím đều mang lại cảm giác mượt mà, phản hồi nhanh nhạy và âm thanh gõ đặc trưng, đầy phấn khích – hoàn hảo cho những phiên gaming căng thẳng hay những giờ làm việc đòi hỏi sự chính xác và thoải mái tối đa. AULA F75 F7514 được chế tác với sự tỉ mỉ, tích hợp cấu trúc Gasket Mount tiên tiến giúp tối ưu hóa cảm giác gõ, giảm thiểu tiếng ồn và mang lại sự ổn định vượt trội. Bộ keycap PBT bền bỉ không chỉ chống bóng mờ hiệu quả mà còn giữ màu sắc sống động qua thời gian. Khả năng kết nối đa dạng (có dây, Bluetooth và 2.4Ghz) mang đến sự linh hoạt tuyệt đối, cho phép bạn dễ dàng chuyển đổi giữa các thiết bị. AULA F75 F7514 Đen + Hồng Gradient/Reaper switch không chỉ là một chiếc bàn phím, mà là người bạn đồng hành tin cậy, giúp bạn khẳng định phong cách và chinh phục mọi thử thách với hiệu suất không ngừng nghỉ.',
        1, 'https://product.hstatic.net/200000722513/product/mode-phien-ban-den-hong-gradientreaper-switch_20252251159292-photoroom_9017af59091746b29ecfcae9a773371a.png'
    );
