INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ cứng di dộng SSD ASUS TUF Gaming AS1000, 1 TB, USB-C 3.2 Gen2, 1250 MB/s', 'o-cung-di-dong-asus-tuf-gaming-as1000', 3,
        '{"Giao thức kết nối": "USB-C 3.2 Gen 2", "Dung lượng": "1TB", "Kích thước": "125 x 54 x 13 mm", "Trọng lượng": "157 gam", "Tốc độ": "Lên tới 1.250MB/s (10Gbps)", "Chứng nhận bảo vệ": "IP68MIL-STD-810H 516.8 Drop Test", "Phụ kiện": "Hướng dẫn cài đặt nhanhCáp USB-C sang USB-CHướng dẫn sử dụng"}',
        'Bước vào thế giới của tốc độ không giới hạn và độ bền vượt trội với Ổ cứng di động SSD ASUS TUF Gaming AS1000 1 TB. Được thiết kế dành riêng cho những game thủ, nhà sáng tạo nội dung và người dùng chuyên nghiệp đòi hỏi hiệu suất tối đa, AS1000 là sự kết hợp hoàn hảo giữa sức mạnh và sự bền bỉ. Với giao tiếp USB-C 3.2 Gen2 tiên tiến, AS1000 mang đến tốc độ đọc/ghi đáng kinh ngạc lên đến 1250 MB/s. Điều này có nghĩa là bạn có thể tải game siêu nhanh, chỉnh sửa video 4K mượt mà, hoặc truyền tải các tập tin dung lượng lớn chỉ trong tích tắc, giúp tối ưu hóa mọi công việc và giải trí. Được thừa hưởng DNA của dòng TUF Gaming, ổ cứng này không chỉ mạnh mẽ về tốc độ mà còn cực kỳ kiên cố. Nó được chế tạo để chịu đựng những điều kiện khắc nghiệt nhất, với khả năng chống sốc vượt trội, bảo vệ dữ liệu quý giá của bạn khỏi va đập và rung lắc. Dù bạn đang di chuyển đến các giải đấu, làm việc tại quán cà phê hay khám phá những vùng đất mới, AS1000 luôn sẵn sàng đồng hành cùng bạn một cách an toàn. Dung lượng 1 TB cung cấp không gian lưu trữ rộng rãi cho toàn bộ thư viện game, dự án sáng tạo, phim ảnh và tài liệu quan trọng của bạn. Thiết kế nhỏ gọn, chắc chắn với vỏ ngoài đậm chất gaming không chỉ dễ dàng bỏ vào túi mà còn tạo nên phong cách riêng. Ổ cứng di động SSD ASUS TUF Gaming AS1000 1 TB là lựa chọn lý tưởng cho bất kỳ ai cần một giải pháp lưu trữ di động siêu nhanh, siêu bền và đáng tin cậy. Nâng tầm trải nghiệm lưu trữ của bạn ngay hôm nay!',
        1, 'https://product.hstatic.net/200000722513/product/fwebp__9__06afa03f7dde434a90ad64cbdc10e6d8.png'
    );
