INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ cứng di dộng SSD SamSung T5 EVO 2TB, USB 3.2 Gen1, Đen (MU-PH2T0S/WW)', 'o-cung-di-dong-ssd-samsung-t5-evo-2tb-mu-ph2t0s-ww', 16,
        '{"Thương hiệu": "Samsung", "Tên sản phẩm": "Ổ cứng di động SamSung T5 EVO 2TB, USB 3.2 Gen1", "Bảo hành": "36 Tháng", "Giao thức kết nối": "USB 3.2 Gen 1 (5 Gbps)", "Dung lượng": "2TB", "Kích thước": "40 x 95 x 17 mm", "Trọng lượng": "102g", "Tốc độ": "Đọc: Lên đến 460 MB/sGhi:&nbsp;Lên đến 460 MB/s"}',
        'Khám phá sức mạnh và sự tiện lợi tối ưu với Ổ cứng di động SSD Samsung T5 EVO 2TB, một thiết bị lưu trữ đỉnh cao được thiết kế để đáp ứng mọi nhu cầu của bạn. Với dung lượng khổng lồ 2TB, T5 EVO cung cấp không gian rộng rãi để bạn lưu trữ hàng ngàn bức ảnh chất lượng cao, video 4K sắc nét, bộ sưu tập game đồ sộ, và tất cả các tệp công việc quan trọng của mình mà không cần lo lắng về không gian. Được trang bị giao diện USB 3.2 Gen1, T5 EVO mang đến tốc độ truyền dữ liệu cực nhanh, cho phép bạn di chuyển các tệp lớn chỉ trong chớp mắt. Hãy quên đi thời gian chờ đợi và tận hưởng hiệu suất làm việc liền mạch, dù bạn đang sao lưu dữ liệu, chỉnh sửa video trực tiếp từ ổ đĩa hay chạy các ứng dụng nặng. Với thiết kế nhỏ gọn, bền bỉ và màu đen sang trọng (MU-PH2T0S/WW), T5 EVO không chỉ là một công cụ lưu trữ mà còn là một phụ kiện công nghệ đẳng cấp. Vỏ ngoài chắc chắn bảo vệ dữ liệu của bạn khỏi những va đập không mong muốn, trong khi kích thước tiện lợi cho phép bạn dễ dàng mang theo bên mình mọi lúc mọi nơi – từ văn phòng đến những chuyến du lịch xa. Samsung T5 EVO 2TB là lựa chọn hoàn hảo cho các nhiếp ảnh gia, nhà quay phim, game thủ, và bất kỳ ai cần một giải pháp lưu trữ di động đáng tin cậy, tốc độ cao và dung lượng lớn. Tận hưởng sự an tâm với công nghệ SSD tiên tiến của Samsung, đảm bảo dữ liệu của bạn luôn an toàn và sẵn sàng mọi lúc.',
        1, 'https://product.hstatic.net/200000722513/product/vn-portable-ssd-t5-evo-mu-ph2t0s_8007858882c04bde80db2e9a9f820f82.png'
