INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ cứng di dộng SSD SamSung T7 Portable 1TB, 2.5 inch, USB TypeC 3.2, Xám (MU-PC1T0T/WW)', 'o-cung-di-dong-ssd-samsung-t7-portable-1tb-mu-pc1t0t-ww', 16,
        '{"Thương hiệu": "Samsung", "Tên sản phẩm": "Ổ cứng di động SSD T7 USB 3.2 Gen 2 1TB", "Bảo hành": "36 Tháng", "Giao thức kết nối": "USB 3.2 Gen.2 (10Gbps)", "Dung lượng": "1TB", "Kích thước": "85 x 57 x 8 mm", "Trọng lượng": "58g", "Tốc độ": "Đọc: Lên tới 1.050MB/s *Ghi:&nbsp;Lên tới 1.000MB/s **Hiệu suất có thể thay đổi tùy vào môi trường hệ thống ngay cả với giao diện USB 3.2 với hỗ trợ UASP)"}',
        'Khám phá đỉnh cao của hiệu suất và tính di động với Ổ cứng di động SSD Samsung T7 Portable 1TB (MU-PC1T0T/WW) màu Xám thanh lịch. Được thiết kế dành cho những người dùng yêu cầu tốc độ vượt trội, độ bền bỉ đáng kinh ngạc và khả năng bảo mật tối ưu, T7 chính là giải pháp lưu trữ hoàn hảo cho mọi nhu cầu của bạn.\n\nVới công nghệ SSD tiên tiến và giao diện USB Type-C 3.2 Gen 2 siêu tốc, ổ cứng T7 mang đến tốc độ đọc/ghi dữ liệu ấn tượng lên đến 1.050 MB/s và 1.000 MB/s tương ứng. Điều này có nghĩa là bạn có thể di chuyển các tập tin lớn, video 4K hay các dự án phức tạp chỉ trong tích tắc, tiết kiệm thời gian quý báu cho công việc và giải trí.\n\nDung lượng 1TB rộng rãi cho phép bạn lưu trữ hàng ngàn bức ảnh, video, tài liệu và các ứng dụng quan trọng mà không cần lo lắng về không gian. Với kích thước cực kỳ nhỏ gọn, nhẹ như một chiếc thẻ tín dụng và trọng lượng chỉ khoảng 58 gram, T7 dễ dàng nằm gọn trong lòng bàn tay hoặc túi áo, biến nó thành người bạn đồng hành lý tưởng cho mọi chuyến đi.\n\nSamsung T7 không chỉ nhanh mà còn cực kỳ bền bỉ. Vỏ kim loại nguyên khối chắc chắn cùng thiết kế không có bộ phận chuyển động giúp nó chịu được va đập từ độ cao lên tới 2 mét, bảo vệ dữ liệu quý giá của bạn khỏi những rủi ro không mong muốn. Thêm vào đó, tính năng bảo mật bằng mật khẩu với mã hóa phần cứng AES 256-bit đảm bảo rằng mọi thông tin cá nhân và dữ liệu nhạy cảm của bạn luôn được an toàn tuyệt đối.\n\nTương thích rộng rãi với máy tính cá nhân (PC), Mac, thiết bị Android và cả các máy chơi game, Samsung T7 Portable SSD 1TB là lựa chọn lý tưởng cho các nhiếp ảnh gia, nhà quay phim, sinh viên hay bất kỳ ai cần một giải pháp lưu trữ nhanh chóng, an toàn và cực kỳ tiện lợi trên mọi hành trình.',
        1, 'https://product.hstatic.net/200000722513/product/vn-portable-ssd-t7-mu-pc1t0t-ww__7__6ee3aeca2a4a4ff29da97a9c8e25cd7f.png'
    );
