INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ cứng di dộng SSD SamSung T7 Portable 500GB, 2.5 inch, USB TypeC 3.2, Xanh (MU-PC500H/WW)', 'o-cung-di-dong-ssd-samsung-t7-portable-500gb-mu-pc500h-ww', 16,
        '{"Thương hiệu": "Samsung", "Tên sản phẩm": "Ổ cứng di động SSD T7 USB 3.2 Gen 2 500GB", "Bảo hành": "36 Tháng", "Giao thức kết nối": "USB 3.2 Gen.2 (10Gbps)", "Dung lượng": "500GB", "Kích thước": "85 x 57 x 8 mm", "Trọng lượng": "58g", "Tốc độ": "Đọc: Lên tới 1.050MB/s *Ghi:&nbsp;Lên tới 1.000MB/s **Hiệu suất có thể thay đổi tùy vào môi trường hệ thống ngay cả với giao diện USB 3.2 với hỗ trợ UASP)"}',
        'Khám phá sức mạnh và sự tiện lợi tối ưu với Ổ cứng di động SSD Samsung T7 Portable 500GB màu Xanh (MU-PC500H/WW) – giải pháp lưu trữ lý tưởng cho mọi nhu cầu di chuyển của bạn. Được thiết kế để đáp ứng tốc độ và độ bền vượt trội, T7 không chỉ là một thiết bị lưu trữ, mà còn là người bạn đồng hành đáng tin cậy cho dữ liệu quý giá của bạn.\n\nVới công nghệ SSD tiên tiến, Samsung T7 mang lại tốc độ truyền dữ liệu đáng kinh ngạc: lên đến 1.050 MB/s cho tốc độ đọc và 1.000 MB/s cho tốc độ ghi. Điều này có nghĩa là bạn có thể di chuyển các tệp tin lớn, video 4K hay ảnh RAW dung lượng cao chỉ trong tích tắc, tiết kiệm thời gian quý báu của mình cho công việc hoặc giải trí.\n\nDung lượng 500GB là không gian hoàn hảo để bạn lưu trữ hàng ngàn tài liệu, ảnh, video và ứng dụng mà không cần lo lắng về việc hết chỗ. Kết nối USB Type-C 3.2 Gen 2 đảm bảo khả năng tương thích rộng rãi với PC, Mac, thiết bị Android, máy chơi game và hơn thế nữa, đồng thời tối ưu hóa hiệu suất truyền dữ liệu một cách hiệu quả nhất.\n\nT7 nổi bật với thiết kế siêu nhỏ gọn, chỉ bằng một chiếc thẻ tín dụng và cực kỳ mỏng nhẹ, dễ dàng nằm gọn trong túi áo hoặc túi xách. Vỏ nhôm nguyên khối bền bỉ, tông màu Xanh trẻ trung, sang trọng không chỉ chống sốc hiệu quả khi rơi từ độ cao lên đến 2 mét, mà còn tản nhiệt tối ưu, giữ cho ổ đĩa hoạt động ổn định và bền bỉ theo thời gian.\n\nBảo mật dữ liệu của bạn là ưu tiên hàng đầu. Samsung T7 được trang bị tính năng bảo mật bằng mật khẩu với mã hóa phần cứng AES 256-bit, giúp bảo vệ thông tin cá nhân và dữ liệu quan trọng của bạn khỏi những truy cập trái phép, mang lại sự an tâm tuyệt đối.\n\nDù bạn là một nhiếp ảnh gia, nhà quay phim chuyên nghiệp, sinh viên cần mang theo tài liệu, hay đơn giản chỉ cần một giải pháp lưu trữ nhanh chóng và an toàn, Samsung T7 Portable 500GB màu Xanh chính là lựa chọn hoàn hảo. Nâng tầm trải nghiệm lưu trữ của bạn ngay hôm nay với sự kết hợp hoàn hảo giữa hiệu năng, độ bền và phong cách!',
        1, 'https://product.hstatic.net/200000722513/product/vn-portable-ssd-t7-mu-pc500h-ww__7__98d79726fa7c45b5a0cfd5cb0a39700a.png'
    );
