INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ cứng di động SSD Transcend ESD310C 1TB, 1050MB/s, Đen', 'o-cung-di-dong-ssd-transcend-esd310c-1tb-den', 42,
        '{}',
        'Giải phóng tiềm năng dữ liệu của bạn với Ổ cứng di động SSD Transcend ESD310C 1TB, một sự kết hợp hoàn hảo giữa tốc độ, dung lượng và sự tiện lợi vượt trội. Với thiết kế nhỏ gọn, tinh tế màu đen, ESD310C không chỉ là một thiết bị lưu trữ mà còn là người bạn đồng hành lý tưởng cho mọi nhu cầu công việc và giải trí của bạn. Trải nghiệm tốc độ truyền dữ liệu chóng mặt lên đến 1050MB/s, cho phép bạn sao chép các tệp lớn, video 4K hay thư viện ảnh đồ sộ chỉ trong tích tắc. Không còn phải chờ đợi, năng suất của bạn sẽ được nâng cao đáng kể dù bạn là nhà sáng tạo nội dung, game thủ hay chuyên gia cần truy cập dữ liệu tức thì. Dung lượng 1TB rộng rãi cung cấp không gian thoải mái để lưu trữ hàng ngàn tài liệu, phim ảnh, trò chơi mà không lo hết chỗ. Transcend ESD310C được trang bị giao diện USB 10Gbps (USB 3.2 Gen 2) và đầu nối USB Type-C tích hợp, đảm bảo khả năng tương thích rộng rãi với các thiết bị hiện đại từ máy tính xách tay, điện thoại thông minh, máy tính bảng đến máy chơi game console. Thiết kế siêu nhỏ gọn, chỉ bằng một chiếc USB thông thường, cho phép bạn dễ dàng bỏ vào túi áo hay túi xách, mang theo mọi lúc mọi nơi mà không hề vướng víu. Được chế tạo với công nghệ SSD tiên tiến, ESD310C bền bỉ hơn nhiều so với ổ cứng truyền thống, chống sốc tốt hơn và hoạt động yên tĩnh tuyệt đối. Đây là giải pháp lưu trữ đáng tin cậy, an toàn cho những dữ liệu quý giá nhất của bạn. Hãy nâng tầm trải nghiệm lưu trữ của bạn với Transcend ESD310C 1TB – tốc độ đỉnh cao, dung lượng không giới hạn và sự di động tối ưu, tất cả trong một thiết bị sang trọng, mạnh mẽ.',
        1, 'https://product.hstatic.net/200000722513/product/pp_esd310c_01_2t_34f5e3d3351f47bdb8402b12634636d2.png'
    );
