INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ cứng di động SSD Transcend ESD310C 512GB, 1050MB/s, Đen', 'o-cung-di-dong-ssd-transcend-esd310c-512gb-den', 42,
        '{}',
        'Giới thiệu Ổ cứng di động SSD Transcend ESD310C 512GB – giải pháp lưu trữ tối ưu dành cho những ai đòi hỏi tốc độ, dung lượng và sự tiện lợi tuyệt đối. Với thiết kế nhỏ gọn đầy ấn tượng cùng màu đen sang trọng, ESD310C không chỉ là một thiết bị lưu trữ mà còn là một phụ kiện công nghệ đẳng cấp. Trải nghiệm hiệu suất vượt trội với tốc độ đọc/ghi đáng kinh ngạc lên đến 1050MB/s. Dù bạn đang chuyển các tệp tin lớn, chỉnh sửa video 4K trực tiếp từ ổ đĩa, hay chạy các ứng dụng đòi hỏi cao, ESD310C đều đảm bảo mọi tác vụ diễn ra mượt mà, không gián đoạn. Dung lượng 512GB cung cấp không gian rộng rãi để bạn lưu trữ hàng ngàn bức ảnh, vô số video chất lượng cao, các dự án công việc quan trọng hay thư viện game yêu thích của mình. Điểm nổi bật của Transcend ESD310C chính là thiết kế đầu nối kép USB Type-A và Type-C tích hợp. Điều này cho phép bạn kết nối trực tiếp với hầu hết các thiết bị, từ máy tính xách tay, PC cho đến máy tính bảng và điện thoại thông minh (hỗ trợ OTG) mà không cần dùng đến cáp chuyển đổi rườm rà. Chỉ cần cắm và sử dụng! Với kích thước nhỏ gọn như một chiếc USB và trọng lượng siêu nhẹ, ESD310C dễ dàng nằm gọn trong túi áo hay ví của bạn, sẵn sàng đồng hành cùng bạn mọi lúc mọi nơi. Là một ổ SSD, thiết bị này không có bộ phận chuyển động, mang lại khả năng chống sốc và độ bền vượt trội, bảo vệ dữ liệu quý giá của bạn an toàn trong mọi hành trình. Transcend ESD310C 512GB là lựa chọn hoàn hảo cho các nhiếp ảnh gia, nhà quay phim, game thủ, sinh viên hay bất kỳ ai cần một giải pháp lưu trữ nhanh chóng, đáng tin cậy và cực kỳ linh hoạt. Nâng tầm trải nghiệm lưu trữ của bạn ngay hôm nay với Transcend ESD310C!',
        1, 'https://product.hstatic.net/200000722513/product/pp_esd310c_01_2t_049cdd2bedb942bea61d1de0385616fa.png'
    );
