INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ Cứng HDD WD 2TB Black', 'wd-hdd-2tb-black-7200rpm', 32,
        '"{\"Nhà sản xuất\": \"Western Digital\", \"Bảo hành\": \"60 Tháng\", \"Chuẩn giao tiếp\": \"Sata III\", \"Kích thước\": \"26.1 x147x101.6 mm (Cao x Dài x Rộng\", \"Dung lượng\": \"2 TB\", \"Cache\": \"64M Cache\", \"RPM\": \"7200 rpm\", \"Tốc độ đọc\": \"~100MB/s\", \"Tốc độ ghi\": \"~100MB/s\", \"Trọng lượng\": \"0.60 kg\"}"',
        'Cho dù bạn là một nhiếp ảnh gia, biên tập video hay nghệ sĩ kỹ thuật số hoặc muốn xây dựng một hệ thống của riêng bạn, thì WD black chính là giải pháp của bạn.Ổ đĩa WD Black mang lại hiệu suất tuyệt vời trong việc lưu trữ các tệp đa phương tiện lớn như ảnh, video và các ứng dụng nặng.Dành cho các game thủNgoài hiệu năng hoàn hảo để chơi game, các ổ WB Black cung cấp dung lượng lưu trữ tuyệt vời cho các thư viện chơi game mỡ rộng của bạn, chẳng phải lo ngại về các game offline bom tấn với đồ họa và data khổng lồ. WD Black kết hợp với SSD sẽ cho PC bạn hiệu suất tối đa.Truyền dữ liệu tốc độ caoVới 256MB bộ nhớ cache trên các mô hình 4TB và 6TB, công nghệ bộ đệm động của WD cải thiện hiệu suất trong thời gian thực để phân bổ và tối ưu hóa bộ đệm giữ đọc và ghi.Xử lý lõi képỔ đĩa WD Black có bộ xử lý lõi kép cung cấp khả năng xử lý gấp đôi so với bộ xử lý lõi đơn tiêu chuẩn để tối đa hóa hiệu suất ổ đĩa, đặc biệt là để tải các trò chơi hoặc les đa phương tiện lớn.Tăng độ tin cậyCác ổ WD Black được trang bị Công nghệ StableTrac (2 TB và các model lớn hơn). Điều này đảm bảo trục động cơ bên trong ổ cứng để tăng độ tin cậy và giúp giảm tác động do rung động do hệ thống từ các thành phần khác.',
        1, 'https://product.hstatic.net/200000722513/product/hdd_wd_2tb_black_376ecdc3d4ab4616a27cdb28c92b9025_2e0c583d482e441982b77d7c3ba76bc2.jpg'
    );
