INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ Cứng HDD Seagate 1TB 2.5" for Laptop - ST1000LM048', 'o-cung-hdd-seagate-1tb-2-5inch-sata-3-st1000lm048', 33,
        '"{\"Thương hiệu\": \"SeaGate\", \"Tên sản phẩm\": \"Ổ cứng HDD Seagate 1TB Barracuda 2.5inch SATA 3 - ST1000LM048\", \"Bảo hành\": \"24 Tháng\", \"Giao thức kết nối\": \"SATA3 - 2.5\\\"\", \"Dung lượng\": \"1 TB\", \"Tốc độ quay\": \"5400 rpm\", \"Bộ nhớ cache\": \"128 Mb\", \"Kích thước\": \"100 x 69,9 x 7 mm\", \"Kiểu dáng\": \"2.5\\\"\", \"Tốc độ đọc\": \"Lên tới 140Mb/s\", \"Tốc độ ghi\": \"Lên tới 140Mb/s\", \"Tính năng nổi bật\": \"- Với tốc độ xử lí dữ liệu nhanh gấp 5 lần những ổ cứng thông thường thì việc trải nghiệm game hay những ứng dụng đòi hỏi tốc độ xử lí cao sẽ trở nên đơn giản hơn. Kết nối SATA tốc độ cao lên tới 6Gb/s.- Tính năng Green Hybrid ™ giúp giảm mức tiêu thụ năng lượng của thiết bị, giúp người dùng tăng tuổi thọ ổ cứng của họ và giảm chi phí của họ với một ổ cứng tiết kiệm năng lượng.- Multi-Tier Caching Technology™ (MTC) nâng cao hiệu suất bằng cách sử dụng NAND flash, DRAM và công nghệ media caching, cho phép người dùng tải ứng dụng và các tập tin thậm chí nhanh hơn.- Adaptive Memory™ là công nghệ xác định dữ liệu thường xuyên truy cập, do đó cung cấp dữ liệu nhanh hơn, tiêu thụ ít năng lượng hơn và kéo dài tuổi thọ ổ cứng.\"}"',
        'Với chất lượng vượt trội và hiệu năng cao. HDD Seagate Barracuda&nbsp;là một sự lựa chọn tối ưu khi bạn muốn nâng cấp ổ cứng.Dung lượng cao và tốc độ đọc ghi nhanh hơn 5 lần ổ cứng thông thường. Giờ đây mọi thao tác trên máy tính của bạn diễn ra một cách nhanh chóng và mượt mà nhất có thể. Tăng hiệu năng toàn bộ của máy tính lên rất nhiều.',
        1, 'https://product.hstatic.net/200000722513/product/te-1tb-2-5inch-sata-3-st1000lm048-666_4b01840fcb6e4de1b456700365dd9916_9a99a5a20878418eb1e98509f208c101.jpg'
    );
