INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Leobog AMG65 TM (Trắng/ Jadeite switch) AMG6502', 'ban-phim-leobog-amg65-tm-trang-jadeite-switch-amg6502', 21,
        '"{}"',
        'Đánh giá chi tiết&nbsp;bàn phím Leobog AMG65 TM (Trắng/ Jadeite switch) AMG6502Leobog, một thương hiệu bàn phím vô cùng thân thuộc với cộng động yêu thích bàn phím custom ngoài ra còn là những bộ kit bàn phím, switch hay keycap chất lượng. Và nay thương hiệu đến từ Trung Quốc đã xuất hiện tại thị trường Việt Nam với chiếc bàn phím được gia công từ chính dây chuyền sản xuất của mình. Sau đây, GEARVN sẽ cùng bạn đánh giá về chiếcbàn phím LeobogAMG65 TM.Ngoại hình đẹp mắt với những gam màu độc đáoLeobog AMG65 TMđặt ngay vào mắt chúng ta là một ngoại hình vô cùng bắt mắt. Đó là sự hòa quyện giữa màu trắng và đen, kèm theo là những nét vẽ vô cùng trẻ trung trên những chiếc keycap. Nhắc đến keycap thì chiếcbàn phím cơtừ Leobog đem đến cho chúng ta font chữ và hình ảnh vô cùng tinh nghịch.Keycap bắt mắt tạo nên từ chất liệu cao cấpTừng chiếckeycaptrên Leobog AMG65 TM được thương hiệu tỉ mỉ làm từ chất liệu nhựa PBT cứng cáp. Các kí tự trên keycap được khắc theo công nghệ Doubleshot nhằm tạo nên sự bền bỉ trong quá trình sử dụng, hạn chế tối đa tình trạng bị mờ hoặc bị mất ký tự sau thời gian dài sử dụng.Switch Jadeite trầm ấm với hot-swap 5 pinLeobog AMG65 TM sẽ không làm bạn thất vọng với kinh nghiệm chinh chiến qua rất nhiều sản phẩm bàn phím cơ hay switch chất lượng trên thị trường. Để mang đến “chiến binh” mạnh mẽ nhất thì nhà Leobog đã trang bị cho chiếc bàn phím của mình switch Jadeite đem lại chất âm vô cùng ấm với chiếc switch linear. Chất âm thocky từ Leobog AMG65 TM đem lại vô cùng phù hợp cho môi trường văn phòng, đặc biệt là nhu cầu gõ văn bản thường xuyênHot-swap 5 pin trên Leobog AMG65 TM cho phép người dùng thoải mái tùy biến chiếc bàn phím với những chiếc switch trên thị trường để phù hợp với nhu cầu sử dụng và đôi tay của người dùng. Qua đó, bạn có thể tạo nên một chiếc bàn phím tốt nhất và phù hợp nhất dành cho nhu cầu sử dụng của mình.',
        1, 'https://product.hstatic.net/200000722513/product/amg65_a6a5e42ac0e84a1db2ce578bfce839e5.png'
    );
