INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím AULA F75 Ice Blue', 'ban-phim-aula-f75-ice-blue-xanh-nhat-trang-xanh-dam-ice-vein-switch', 22,
        '"{\"Tóm lại, bàn phím AULA F75 Ice Blue là một sản phẩm xuất sắc hội tụ đầy đủ những yếu tố cần thiết cho mộtbàn phím gaminghoàn hảo\": \"từ thiết kế đẹp, tính năng đa dạng cho đến cảm giác bấm êm ái. Đây thực sự là lựa chọn lý tưởng cho những ai đam mê công nghệ và chơi game. Nếu bạn đang tìm kiếm một bàn phím có tính năng vượt trội và thiết kế tinh tế, AULA F75 chính là sự lựa chọn không thể bỏ lỡ.\"}"',
        'Bàn phím AULA F75 Ice Blue (Xanh nhạt + Trắng + Tím đậm / Ice Vein Switch) là một sản phẩm mang đến cho người dùng trải nghiệm gõ phím tuyệt vời không chỉ nhờ thiết kế đẹp mắt mà còn do tính năng vượt trội. Với lối thiết kế tinh tế, ánh sáng RGB độc đáo và nhiều chế độ kết nối linh hoạt, đây được xem là một trong những sản phẩm không thể thiếu cho những game thủ và người yêu công nghệ hiện đại.',
        1, 'https://product.hstatic.net/200000722513/product/aula-f75-ban-phim-co-gaming-phie__6_-photoroom_6d6bb2efd7174f3abb6bbd219083cea4.png'
    );
