INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Webcam Rapoo XW180 1080P', 'webcam-rapoo-xw180', 29,
        '{"Thương hiệu": "Rapoo", "Model": "XW180", "Độ phân giải": "Full HD 1080P/ HD 720P", "Tỷ lệ khung hình": "30 FPS", "Phạm vi quan sát": "80 độ", "Mic tích hợp": "Mic kép khử tiếng ồn", "Kết nối": "USB 2.0", "Tiêu cự": "Fixed", "Hệ điều hành hổ trợ": "Win, macOS, Chrome OS, Android", "Kích thước": "77x49x45&nbsp;mm", "Trọng lượng": "98g", "Màu sắc": "Đen"}',
        'Đánh giá chi tiếtBạn cần một chiếcwebcamthu hình sắc nét với góc nhìn rộng, vậy sao bạn không thử tham khảoWebcam Rapoo XW180 1080Pđang có tạiGEARVNvới công nghệ tiên tiến phù hợp cho cả việc học và công việc mỗi ngày của bạn.Hình ảnh chất lượng với khả năng cân bằng ánh sángSở hữu độ phân giải Full HD 1080P, hình ảnh của bạn khi được thu vào sẽ có độ sắc nét cao cùng với màu sắc tự nhiên. Webcam có khả năng tự lấy nét cũng như cân bằng ánh sáng giúp hình ảnh của bạn không bị lóe sáng, tối hay bị out nét.Góc xoay 360 độ thu mọi hình ảnhPhạm vi quan sát của Webcam Rapoo XW180 1080P lên tới 80 độ, cho phép bắt trọn từng vị trí của bạn và không gây ra hiện tượng bị cắt hình khi sử dụng. Webcam tích hợp thêm cả chế độ xoay ngang 360 độ cho bạn dễ dàng điều chỉnh camera đến vị trí mình muốn mà không cần thêm bất kỳ thiết bị hỗ trợ nào.Mic tích hợp khử tiếng ồnThật thuận tiện khi webcam còn hỗ trợ thêmmicrophonevới khả năng lọc âm cho bạn trong phạm vi lên đến&nbsp;3m. Việc này giúp cho quá trình ghi hình của bạn sẽ không bị vấn đề tạp âm và âm thanh sẽ ổn định trong suốt thời gian sử dụng webcam.Khả năng kết nối thiết bị đa dạngWebcam Rapoo XW180 1080P có thể kết nối với nhiều thiết bị nhưpc gaming,laptophay cả các thiết bị macOS, Android. Webcam còn được trang bị thêm giá gắn đi kèm một tấm giác hút để giúp cố định webcam một cách chắc chắn.',
        1, 'https://product.hstatic.net/200000722513/product/1595845615-3906-e1613393253801_b4d0711105174ce9a0f3850029d8be44.png'
    );
