INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Webcam Logitech Brio 100 Graphite', 'webcam-logitech-brio-100-graphite', 13,
        '{"Độ phân giải": "Full HD 1080p 30fps, HD 720p/30fps", "Kết nối": "USB-A, cắm là sử dụng", "Màu sắc": "Đen", "Tầm nhìn chéo": "58°", "Tự động điều chỉnh ánh sáng": "Right Light 2", "Tính năng": "Có mic tích hợp", "Khả năng thu phóng": "1x"}',
        'Nâng tầm trải nghiệm giao tiếp trực tuyến của bạn với Webcam Logitech Brio 100 Graphite – sự kết hợp hoàn hảo giữa hiệu suất vượt trội, thiết kế tinh tế và tính năng thông minh. Được thiết kế dành cho những ai yêu cầu chất lượng hình ảnh và âm thanh đỉnh cao, Brio 100 mang đến hình ảnh Full HD 1080p sắc nét và sống động ở tốc độ 30 khung hình/giây, đảm bảo mọi cuộc họp, buổi học hay cuộc gọi video đều diễn ra mượt mà và chuyên nghiệp.\n\nVới công nghệ RightLight™ tích hợp, chiếc webcam này tự động điều chỉnh ánh sáng, giúp bạn luôn xuất hiện rõ ràng và nổi bật, ngay cả trong điều kiện ánh sáng yếu hoặc ngược sáng. Micro khử tiếng ồn tích hợp thu âm giọng nói của bạn một cách rõ ràng và tự nhiên, loại bỏ tạp âm gây nhiễu từ môi trường xung quanh, giúp thông điệp của bạn luôn được truyền tải một cách hoàn hảo.\n\nThiết kế màu Graphite sang trọng, nhỏ gọn và kẹp đa năng cho phép bạn dễ dàng gắn Brio 100 lên bất kỳ màn hình laptop, desktop hoặc tripod nào. Kết nối ''plug-and-play'' qua USB giúp việc cài đặt trở nên đơn giản và nhanh chóng trên các hệ điều hành Windows, macOS và ChromeOS. Logitech Brio 100 Graphite là lựa chọn lý tưởng cho công việc từ xa, học tập trực tuyến, hoặc bất kỳ ai tìm kiếm một webcam đáng tin cậy mang lại chất lượng hình ảnh và âm thanh vượt trội.',
        1, 'https://product.hstatic.net/200000722513/product/3usj2zof_63f556249f4d49ddae03af75f202ef20.png'
    );
