INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Webcam Logitech Brio 100 Rose', 'webcam-logitech-brio-100-rose', 13,
        '{"Độ phân giải": "Full HD 1080p 30fps, HD 720p/30fps", "Kết nối": "USB-A cắm là dùng", "Màu sắc": "Hồng", "Tầm nhìn chéo": "58°", "Tự động điều chỉnh ánh sáng": "Right Light 2", "Tính năng": "Có mic tích hợp", "Khả năng thu phóng": "1x"}',
        'Nâng tầm phong cách và chất lượng giao tiếp trực tuyến của bạn với Webcam Logitech Brio 100 Rose – sự kết hợp hoàn hảo giữa vẻ đẹp tinh tế và công nghệ vượt trội. Khoác lên mình sắc hồng ''Rose'' độc đáo, chiếc webcam này không chỉ là một thiết bị công nghệ mà còn là điểm nhấn thời trang trên bàn làm việc của bạn, thể hiện cá tính riêng biệt. Brio 100 Rose mang đến chất lượng hình ảnh Full HD 1080p sắc nét, mượt mà ở tốc độ 30 khung hình/giây, đảm bảo bạn luôn xuất hiện rạng rỡ và rõ ràng trong mọi cuộc gọi video, buổi học trực tuyến hay buổi họp công việc quan trọng. Công nghệ RightLight 2 tiên tiến của Logitech tự động điều chỉnh ánh sáng, giúp bạn luôn nổi bật ngay cả trong điều kiện thiếu sáng hoặc ánh sáng ngược, không còn lo lắng về việc gương mặt bị tối hay mờ nhạt. Đặc biệt, màn che riêng tư tích hợp cho phép bạn dễ dàng đóng lại ống kính khi không sử dụng, đảm bảo sự an toàn và riêng tư tuyệt đối. Với micro giảm ồn tích hợp, giọng nói của bạn sẽ được thu lại rõ ràng, tách biệt khỏi tạp âm xung quanh, giúp mọi cuộc trò chuyện trở nên liền mạch và hiệu quả hơn. Thiết kế ''plug-and-play'' đơn giản, chỉ cần cắm vào cổng USB là có thể sử dụng ngay, tương thích rộng rãi với các nền tảng họp trực tuyến phổ biến. Logitech Brio 100 Rose không chỉ là một chiếc webcam, mà là tuyên ngôn về phong cách và chất lượng, giúp bạn tự tin tỏa sáng trong mọi khoảnh khắc kết nối.',
        1, 'https://product.hstatic.net/200000722513/product/c6uf2qmx_83370c17cd444b59a8d835b44b720835.png'
    );
