INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Webcam Logitech Brio 100 White', 'webcam-logitech-brio-100-white', 13,
        '{"Độ phân giải": "Full HD 1080p 30fps, HD 720p/30fps", "Kết nối": "USB-A cắm là sử dụng", "Màu sắc": "Trắng", "Tầm nhìn chéo": "58°", "Tự động điều chỉnh ánh sáng": "Right Light 2", "Tính năng": "Có mic tích hợp", "Khả năng thu phóng": "1x"}',
        'Nâng tầm trải nghiệm giao tiếp trực tuyến của bạn với Webcam Logitech Brio 100 White – sự kết hợp hoàn hảo giữa hiệu suất vượt trội và phong cách tinh tế. Được thiết kế dành riêng cho những ai yêu thích sự chuyên nghiệp và thanh lịch, Brio 100 White mang đến hình ảnh sắc nét, sống động với độ phân giải Full HD 1080p ở tốc độ 30 khung hình/giây. Điều này đảm bảo bạn luôn xuất hiện rõ ràng, tươi sáng và chuyên nghiệp trong mọi cuộc họp, buổi học trực tuyến hay buổi phát sóng trực tiếp.\n\nVới thiết kế màu trắng hiện đại và tối giản, Brio 100 White không chỉ là một thiết bị công nghệ mà còn là điểm nhấn sang trọng, hoàn hảo cho mọi không gian làm việc hay học tập. Camera tích hợp tính năng điều chỉnh ánh sáng tự động, giúp tối ưu hóa hình ảnh ngay cả trong điều kiện ánh sáng kém, trong khi micro tích hợp khả năng giảm tiếng ồn thông minh, thu âm giọng nói của bạn rõ ràng và tự nhiên, loại bỏ tạp âm gây xao nhãng.\n\nThiết lập và sử dụng cực kỳ đơn giản với kết nối USB-C plug-and-play, cho phép bạn bắt đầu cuộc gọi chỉ trong tích tắc mà không cần cài đặt phức tạp. Tương thích liền mạch với các hệ điều hành phổ biến như Windows, macOS và ChromeOS. Dù bạn đang làm việc từ xa, tham gia các buổi học trực tuyến hay kết nối với bạn bè và gia đình, Logitech Brio 100 White sẽ biến mọi khoảnh khắc giao tiếp trở nên chân thực, rõ nét và chuyên nghiệp hơn bao giờ hết. Lựa chọn Brio 100 White để không chỉ nhìn rõ mà còn tự tin thể hiện phong cách của riêng bạn.',
