INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Webcam Logitech Brio 500 Graphite', 'webcam-logitech-brio-500-graphite', 13,
        '{"Độ phân giải": "1080p/30fps hoặc 720p/60fps", "Kết nối": "USB-C", "Màu sắc": "Đen", "Tầm nhìn chéo": "90°", "Tự động điều chỉnh ánh sáng": "RightLight 4", "Tính năng": "Lấy nét tự động", "Khả năng thu phóng": "4x"}',
        'Nâng tầm trải nghiệm họp trực tuyến và sáng tạo nội dung của bạn với Webcam Logitech Brio 500 Graphite – một thiết bị được thiết kế tinh xảo để đáp ứng nhu cầu của kỷ nguyên làm việc kết hợp. Với màu Graphite sang trọng và hiện đại, Brio 500 không chỉ là một công cụ mà còn là một điểm nhấn tinh tế trên bàn làm việc của bạn.\n\nTận hưởng chất lượng hình ảnh Full HD 1080p sắc nét, mang lại độ rõ ràng vượt trội cho mọi cuộc gọi video. Công nghệ RightLight 4 tiên tiến của Logitech tự động điều chỉnh ánh sáng, đảm bảo bạn luôn xuất hiện rạng rỡ và chuyên nghiệp, ngay cả trong điều kiện ánh sáng yếu hoặc ngược sáng. Tính năng RightSight tự động căn khung hình, giữ bạn luôn ở trung tâm cảnh quay, tạo sự tương tác tự nhiên và liền mạch.\n\nBên cạnh hình ảnh xuất sắc, Brio 500 còn được trang bị micro kép chống ồn, thu âm giọng nói của bạn một cách rõ ràng và loại bỏ tiếng ồn xung quanh, giúp thông điệp của bạn luôn được truyền tải trọn vẹn.\n\nĐiểm nổi bật của Brio 500 là chế độ \"Show Mode\" đột phá. Chỉ với một thao tác xoay nhẹ, bạn có thể dễ dàng chia sẻ các tài liệu, bản vẽ hoặc vật thể vật lý trên bàn của mình một cách trực quan cho người đối diện, cực kỳ lý tưởng cho các buổi trình bày hoặc giải thích chi tiết. Màn trập riêng tư tích hợp giúp bảo vệ quyền riêng tư của bạn một cách nhanh chóng và an toàn khi không sử dụng camera.\n\nKết nối dễ dàng qua USB-C, Brio 500 tương thích hoàn hảo với các nền tảng họp trực tuyến phổ biến như Zoom, Microsoft Teams, Google Meet và nhiều ứng dụng khác, mang lại sự tiện lợi tối đa. Logitech Brio 500 Graphite là lựa chọn lý tưởng cho các chuyên gia, người sáng tạo nội dung và bất kỳ ai muốn nâng cấp chất lượng hình ảnh và âm thanh của mình trong mọi không gian làm việc.',
        1, 'https://product.hstatic.net/200000722513/product/thumbphim-recovered1_e65ad5f2a0724d3ba8e79f84ba81d24e_fd2d244a870b47cca01a5e45e2bf8c37.png'
    );
