INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Webcam Logitech Brio 500 Pink', 'webcam-logitech-brio-500-pink', 13,
        '{"Độ phân giải": "1080p/30fps hoặc 720p/60fps", "Kết nối": "USB-C", "Màu sắc": "Đen", "Tầm nhìn chéo": "90°", "Tự động điều chỉnh ánh sáng": "RightLight 4", "Tính năng": "Lấy nét tự động", "Khả năng thu phóng": "4x"}',
        'Webcam Logitech Brio 500 Pink là sự lựa chọn hoàn hảo cho những ai đang tìm kiếm một thiết bị nâng tầm trải nghiệm làm việc từ xa, học tập trực tuyến hay sáng tạo nội dung với phong cách và hiệu suất vượt trội. Với tông màu hồng cá tính và thanh lịch, chiếc webcam này không chỉ là một công cụ mà còn là điểm nhấn thẩm mỹ trên bàn làm việc của bạn. Brio 500 mang đến chất lượng hình ảnh Full HD 1080p ở tốc độ 30 khung hình/giây hoặc 720p ở 60 khung hình/giây, đảm bảo mọi cuộc họp, buổi học hay buổi phát trực tiếp của bạn đều sắc nét và mượt mà. Công nghệ RightLight™ 4 tiên tiến tự động điều chỉnh độ sáng và màu sắc, giúp bạn luôn trông đẹp nhất ngay cả trong điều kiện ánh sáng yếu hoặc ngược sáng. Điểm nổi bật của Brio 500 là tính năng Auto-Framing (tự động căn khung hình) giúp bạn luôn ở giữa khung hình, cho dù bạn di chuyển nhẹ. Chế độ Show Mode sáng tạo cho phép bạn dễ dàng nghiêng camera xuống để hiển thị vật thể trên bàn làm việc chỉ bằng một thao tác, cực kỳ hữu ích cho các buổi thuyết trình hay chia sẻ ý tưởng. Màn trập riêng tư tích hợp giúp bảo vệ quyền riêng tư của bạn một cách tuyệt đối khi không sử dụng. Với hai micrô tích hợp công nghệ giảm tiếng ồn, giọng nói của bạn sẽ được thu âm rõ ràng và tự nhiên, loại bỏ tạp âm gây nhiễu từ môi trường xung quanh. Kết nối USB-C tiện lợi và khả năng cắm là chạy giúp thiết lập nhanh chóng, không cần cài đặt phức tạp. Logitech Brio 500 Pink không chỉ chú trọng đến hiệu năng mà còn hướng tới sự bền vững với việc sử dụng nhựa tái chế trong thiết kế, thể hiện cam kết của Logitech với môi trường. Đây chắc chắn là một người bạn đồng hành đáng tin cậy, mang lại sự chuyên nghiệp và cá tính cho mọi khoảnh khắc trực tuyến của bạn.',
        1, 'https://product.hstatic.net/200000722513/product/avatar_8829e55e34bb4530bb859350cca41427_ad9cfe13ce0b4823b540f9327ecad3f3.png'
    );
