INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Webcam Logitech C505', 'webcam-logitech-c505', 13,
        '{"Sản phẩm:": "Webcam Logitech C505", "Độ phân giải tối đa:": "720p/30fps", "Loại tiêu cự": "Cố định", "Loại thấu kính": "Nhựa", "Micrô tích hợp": "Đơn âm", "Tầm nhìn chéo (dFoV)": "60°", "Kích thước": "Chiều cao: 31,91 mm, Chiều rộng: 72,91 mm, Chiều dày: 66,64 mm, Độ dài dây cáp: 2 m", "Trọng lượng": "75g"}',
        'Webcam Logitech C505 là một giải pháp lý tưởng cho những ai tìm kiếm chất lượng hình ảnh HD và âm thanh rõ ràng trong các cuộc gọi video hàng ngày. Với độ phân giải HD 720p ở tốc độ 30 khung hình/giây, C505 mang đến hình ảnh sắc nét, mượt mà, giúp bạn luôn trông chuyên nghiệp và rõ ràng trong mọi cuộc họp trực tuyến hoặc buổi học từ xa.\n\nĐiểm nổi bật của C505 là micro tích hợp có khả năng thu âm từ khoảng cách lên đến 3 mét, sử dụng công nghệ tạo chùm tia (beamforming) giúp giảm tiếng ồn xung quanh và tập trung vào giọng nói của bạn, đảm bảo mọi lời nói đều được truyền tải rõ ràng và tự nhiên.\n\nDây cáp USB-A dài 2 mét cung cấp sự linh hoạt đáng kể trong việc thiết lập vị trí webcam, dù bạn muốn gắn nó lên màn hình, laptop hay đặt trên một giá đỡ. Thiết kế kẹp đa năng cho phép gắn C505 một cách an toàn và dễ dàng trên nhiều loại màn hình hoặc laptop.\n\nWebcam này hoạt động ngay lập tức chỉ với thao tác cắm và chạy (plug-and-play), không cần cài đặt phần mềm phức tạp, tương thích rộng rãi với các hệ điều hành phổ biến như Windows, macOS và ChromeOS.\n\nLogitech C505 là lựa chọn hoàn hảo cho công việc từ xa, học trực tuyến hoặc giữ liên lạc với gia đình và bạn bè, mang lại trải nghiệm giao tiếp trực tuyến đáng tin cậy và chất lượng.',
