INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Webcam Logitech C925e', 'webcam-logitech-c925e', 13,
        '{"Độ phân giải": "1080p", "Tốc độ khung hình": "30FPS", "Kết nối": "USB-A", "Màu sắc": "Đen", "Tiêu cự": "Lấy nét tự động", "Tính năng": "H.264 - Rightlight™ 2", "Kích thước": "73 x 126 x 45mm", "Khối lượng": "170g"}',
        'Webcam Logitech C925e là một giải pháp hội nghị video chuyên nghiệp, được thiết kế đặc biệt để đáp ứng nhu cầu của các doanh nghiệp và người dùng cần chất lượng hình ảnh và âm thanh vượt trội trong mọi cuộc gọi. Với khả năng quay video Full HD 1080p ở tốc độ 30 khung hình/giây, C925e mang đến hình ảnh sắc nét, rõ ràng và sống động, giúp bạn luôn xuất hiện chuyên nghiệp. Công nghệ RightLight™ 2 của Logitech tự động điều chỉnh ánh sáng, đảm bảo bạn luôn được chiếu sáng tốt ngay cả trong điều kiện ánh sáng yếu hoặc ngược sáng, loại bỏ nỗi lo về môi trường thiếu sáng. C925e không chỉ chú trọng đến hình ảnh mà còn cả âm thanh. Thiết bị được trang bị hai micro đa hướng tích hợp với công nghệ giảm tiếng ồn, thu giọng nói rõ ràng từ mọi góc độ, loại bỏ tạp âm xung quanh và mang lại trải nghiệm giao tiếp liền mạch. Tính năng màn trập bảo mật tích hợp là một điểm cộng lớn, cho phép bạn dễ dàng đóng ống kính khi không sử dụng, đảm bảo sự riêng tư hoàn toàn và an tâm tuyệt đối. Thiết kế cắm và chạy (plug-and-play) qua cổng USB giúp việc cài đặt trở nên cực kỳ đơn giản, chỉ cần cắm vào và sử dụng ngay mà không cần cài đặt phần mềm phức tạp. Kẹp gắn đa năng cho phép C925e dễ dàng gắn lên màn hình laptop, màn hình máy tính để bàn hoặc thậm chí là chân máy ảnh. Webcam này tương thích rộng rãi với hầu hết các ứng dụng hội nghị video hàng đầu như Microsoft Teams, Skype for Business, Zoom, Google Meet và nhiều nền tảng khác, đồng thời được chứng nhận cho các ứng dụng UC phổ biến. Với độ bền cao và hiệu suất ổn định, Logitech C925e là lựa chọn lý tưởng cho các cuộc họp trực tuyến quan trọng, phỏng vấn từ xa hoặc làm việc tại nhà, mang lại trải nghiệm kết nối chuyên nghiệp và đáng tin cậy.',
        1, 'https://product.hstatic.net/200000722513/product/gearvn-webcam-logitech-c925e-22222_6e0f4237da9e4cf5b74dd00788ef62ea_68864c40b7894defa2020a8f17929051.jpg'
    );
