INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Webcam Rapoo C200 HD 720p', 'webcam-rapoo-c200-hd-720p', 29,
        '{"Thương hiệu": "Rapoo", "Model": "C200 HD 720p", "Góc nhìn": "78 độ", "Video Encoding": "H.264", "Độ phân giải": "HD 720p", "Hiệu chỉnh ánh sáng tự động": "Micro kép khử tiếng ồn", "Kết nối": "USB 2.0", "Hệ điều hành tương thích": "Windows 7/8/10 trở lên, Mac OS X 10.6 trở lên, ChromeOS và Android v5.0 trở lên", "Phần mềm tương thích": "Skype, Zoom, Google Hangouts, Face Time for Mac, QQ, WeiXin...", "Kích thước sản phẩm": "126*60*118mm", "Trọng lượng sản phẩm": "97.5g", "Đặc điểm nổi bật": "Micrô đa hướng, Ống kính HD 720p lấy nét bằng tay, Đế/Kẹp linh hoạt, Cáp USB"}',
        'Webcam Rapoo C200 HD 720p là lựa chọn lý tưởng cho mọi nhu cầu giao tiếp trực tuyến, từ các cuộc họp công việc quan trọng, lớp học online cho đến những buổi trò chuyện video thân mật với bạn bè và gia đình. Với độ phân giải HD 720p sắc nét ở tốc độ 30 khung hình/giây, chiếc webcam này mang đến hình ảnh rõ ràng, màu sắc chân thực, giúp bạn luôn xuất hiện một cách chuyên nghiệp và sống động nhất trên màn hình.\n\nĐiểm nổi bật của Rapoo C200 chính là khả năng lấy nét cố định, đảm bảo hình ảnh luôn được duy trì độ nét ổn định mà không cần điều chỉnh thủ công phiền phức. Góc nhìn rộng lên đến 90 độ cho phép thu trọn không gian xung quanh hoặc nhiều người cùng lúc trong khung hình, rất phù hợp cho các cuộc họp nhóm nhỏ, tạo sự tương tác tự nhiên.\n\nTích hợp micro đa hướng giảm nhiễu, Rapoo C200 đảm bảo âm thanh của bạn được thu lại rõ ràng, trong trẻo, loại bỏ tiếng ồn xung quanh không mong muốn. Điều này giúp cuộc trò chuyện trở nên liền mạch và hiệu quả hơn rất nhiều, ngay cả trong môi trường ồn ào.\n\nVề thiết kế, webcam sở hữu kiểu dáng nhỏ gọn, hiện đại và linh hoạt. Chân đế kẹp đa năng cho phép bạn dễ dàng gắn nó lên màn hình máy tính xách tay, màn hình desktop hoặc đặt trên mặt phẳng. Khả năng xoay 360 độ theo chiều ngang và điều chỉnh góc nghiêng giúp bạn dễ dàng tìm được góc quay hoàn hảo nhất cho mọi tình huống.\n\nRapoo C200 là sản phẩm \"Plug & Play\", nghĩa là bạn chỉ cần cắm vào cổng USB là có thể sử dụng ngay mà không cần cài đặt driver phức tạp, tiết kiệm thời gian và công sức. Nó tương thích rộng rãi với hầu hết các hệ điều hành phổ biến như Windows, macOS và các phần mềm gọi video thông dụng như Zoom, Skype, Google Meet, Microsoft Teams.\n\nĐây thực sự là một giải pháp webcam hiệu quả, tiện lợi và đáng tin cậy cho mọi người dùng, mang lại trải nghiệm giao tiếp trực tuyến mượt mà và chất lượng cao.',
