INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Webcam Rapoo C280 2K 1440p', 'webcam-rapoo-c280-2k-1440p', 29,
        '{"Tên sản phẩm:": "Webcam Rapoo C280", "Màu:": "Đen", "Hiệu chỉnh ánh sáng tự động:": "Có", "Tích hợp míc:": "Micro kép khử tiếng ồn", "Kết nối:": "USB 2.0", "Hệ điều hành tương thích:": "Windows 7/8/10 trở lên, Mac OS X 10.6 trở lên, Chrome OS, và Android v5.0 trở lên", "Phần mềm tương thích:": "Skype, Zoom, Google Hangouts, Face Time, Zalo for Mac, QQ, WeiXin…", "Kích thước sản phẩm:": "90*45*50mm", "Trọng lượng sản phẩm:": "118g", "Cảm biến:": "4MP CMOS Sensor", "Độ phân giải tối đa:": "2K/30fps (lên tới 2560*1440 pixel), 1080p/30 hoặc 60 fps (lên tới 1920 x 1080 pixel). 720p/30, 60, hoặc 90 fps (lên tới 1280 x 720 pixel)", "Hỗ trợ mã hóa video:": "MJPEG", "Loại tiêu cự:": "Lấy nét cố định", "Tính năng khác:": "Micrô giảm ồn kép tích hợp đa hướng. Trường nhìn (chéo/ngang/dọc) – Field of View (FoV): 85°/77°/46°. Kẹp phổ dụng phù hợp với máy tính xách tay, LCD hoặc màn hình, Tripod"}',
        'Webcam Rapoo C280 2K 1440p là lựa chọn lý tưởng cho mọi nhu cầu giao tiếp trực tuyến và tạo nội dung chất lượng cao. Với độ phân giải 2K QHD (2560x1440p), chiếc webcam này mang đến hình ảnh cực kỳ sắc nét, chi tiết và sống động, vượt trội so với các webcam Full HD thông thường. Bạn sẽ luôn xuất hiện rõ ràng và chuyên nghiệp trong mọi cuộc họp video, buổi học trực tuyến hay livestream. Tính năng lấy nét tự động (autofocus) tiên tiến đảm bảo hình ảnh của bạn luôn sắc nét, ngay cả khi bạn di chuyển nhẹ. Rapoo C280 còn tích hợp micro giảm tiếng ồn, thu âm giọng nói của bạn một cách rõ ràng và tự nhiên, loại bỏ các tạp âm gây nhiễu, giúp cuộc trò chuyện không bị gián đoạn. Ống kính góc rộng của webcam cho phép bạn thu được khung hình rộng hơn, lý tưởng cho các cuộc họp nhóm hoặc khi bạn muốn hiển thị nhiều không gian hơn. Khả năng điều chỉnh ánh sáng tự động giúp hình ảnh của bạn luôn đủ sáng và cân bằng màu sắc, ngay cả trong điều kiện ánh sáng yếu. Với thiết kế nhỏ gọn, kẹp đa năng, Rapoo C280 dễ dàng gắn lên màn hình máy tính, laptop hoặc đặt trên bàn. Chỉ cần cắm vào cổng USB là có thể sử dụng ngay (plug-and-play), tương thích rộng rãi với các hệ điều hành phổ biến như Windows, macOS và các ứng dụng hội nghị trực tuyến như Zoom, Google Meet, Microsoft Teams, Skype. Đây là công cụ hoàn hảo để nâng tầm trải nghiệm giao tiếp và làm việc từ xa của bạn, đảm bảo bạn luôn có chất lượng hình ảnh và âm thanh tốt nhất.',
        1, 'https://product.hstatic.net/200000722513/product/s-webcam-rapoo-c280-2k-1440p-1_-_copy_fddbe551a5a74dbab04b467b9f5cadb1_e702fadc1bb54bd7ac4965a0c070f873.jpg'
    );
