INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url)
VALUES ('iPad Pro M2 12.9 inch  256GB Wifi ', 'ipad-pro-12-9-inch-2022-m2-wifi-2tb--p538756', 2,
    '{"Thương hiệu": "Apple", "Bảo hành": "12 tháng", "Tên": "iPad Pro 12.9\" M2", "Series": "iPad Pro", "Màu sắc": "Xám", "Màn hình": "", "Loại màn hình": "12.9\" Liquid Retina XDR", "Độ phân giải": "2048 x 2732 Pixels", "Cấu hình": "", "Dung lượng (ROM)": "256GB", "Hệ điều hành": "iPadOS", "RAM": "8GB", "Chip": "Apple M2", "Pin": "Li-Po ~ 10.835 mAh", "Công nghệ pin": "Sạc pin nhanh; Tiết kiệm pin", "Cổng sạc": "USB Type-C", "Camera": "", "Camera sau": "12MP, 10MP", "Camera trước": "12MP", "Kết nối": "", "Wifi": "Wifi", "GPS": "GPS/GNSS", "Bluetooth": "Bluetooth 5.3", "Thiết kế & trọng lượng": "", "Kích thước": "28.06  x 21.49 x 0.64 cm", "Khối lượng": "682g", "Thông tin kích thước": ""}',
    '<p><a href="https://phongvu.vn/c/ipad-pro" target="_blank" rel="noopener noreferrer">iPad Pro</a> 12.9 inch 2022 M2 Wifi 256GB là một trong những chiếc tablet cao cấp nhất hiện nay của Apple. Nhờ sở hữu thiết kế sang trọng, màn hình Liquid Retina XDR tuyệt đẹp, cấu hình mạnh mẽ và nhiều tính năng hữu ích, iPad Pro 12.9 inch 2022 M2 Wifi 256GB luôn sẵn sàng đáp ứng tốt mọi nhu cầu sử dụng của người dùng.</p><h2><strong>Ngoại hình ấn tượng, thiết kế đỉnh cao của Apple</strong></h2><p><a href="https://phongvu.vn/c/may-tinh-bang-apple-ipad" target="_blank" rel="noopener noreferrer">iPad</a> Pro 12.9 inch 2022 M2 Wifi 256GB có thiết kế nguyên khối với khung kim loại nguyên khối chắc chắn và bền bỉ. Mặt lưng máy còn được phủ lớp kính mờ giúp chống bám vân tay và mồ hôi.&nbsp;</p><p>Về ngoại hình, iPad Pro 12.9 inch 2022 M2 có kích thước 28.06 x 21.49 x 0.64 cm và nặng 682g, qua đó mà người dùng có thể dễ dàng mang theo máy đi mọi nơi một cách dễ dàng.&nbsp;</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2024/6/12/a949f3d4-5e2c-4b7f-86b4-27e8a209852e/image.png" alt=" iPad Pro 12.9 inch 2022 M2 có kích thước 28.06 x 21.49 x 0.64 cm và nặng 682g"><figcaption>&nbsp;iPad Pro 12.9 inch 2022 M2 có kích thước 28.06 x 21.49 x 0.64 cm và nặng 682g</figcaption></figure><h2><strong>Màn hình thể hiện hình ảnh sắc nét</strong></h2><p>Điểm nhấn của iPad Pro 12.9 inch 2022 M2 Wifi 256GB là màn hình Liquid Retina XDR mini-LED với kích thước 12.9 inch. Màn hình này có độ phân giải 2048 x 2732 pixels, mật độ điểm ảnh 264 ppi, cho hình ảnh sắc nét, sống động. Thêm vào đó, màn hình Liquid Retina XDR còn có độ sáng lên tới 1600 nits, hỗ trợ HDR10, Dolby Vision, True Tone và ProMotion, mang đến trải nghiệm xem phim, chơi game tuyệt vời.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2024/6/12/835e6353-fffa-4c58-9403-05b79f2925a9/image.png" alt="Màn hình này có độ phân giải 2048 x 2732 pixels, mật độ điểm ảnh 264 ppi"><figcaption>Màn hình này có độ phân giải 2048 x 2732 pixels, mật độ điểm ảnh 264 ppi</figcaption></figure><h2><strong>Hiệu năng cực kỳ mạnh mẽ</strong></h2><p>iPad Pro 12.9 inch 2022 M2 Wifi 256GB được trang bị chip <a href="https://phongvu.vn/c/san-pham-apple" target="_blank" rel="noopener noreferrer">Apple</a> M2, là con chip mới nhất của Apple được ra mắt vào tháng 3 năm 2022. Chip M2 được xây dựng trên tiến trình 5nm và có nhiều cải tiến về hiệu năng so với thế hệ chip M1 trước đó.</p><p>Cụ thể, chip M2 có CPU nhanh hơn 18% và GPU nhanh hơn 35% so với chip M1. Ngoài ra, chip M2 còn có Neural Engine mới với 16 nhân, giúp nâng cao hiệu suất xử lý các tác vụ AI và machine learning.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2024/6/12/2c55c427-3a2c-48c3-905f-aa84b5d6d50d/image.jpeg" alt=" iPad Pro 12.9 inch 2022 M2 Wifi 256GB có thể xử lý tốt mọi tác vụ, từ các tác vụ cơ bản như lướt web"><figcaption>&nbsp;iPad Pro 12.9 inch 2022 M2 Wifi 256GB có thể xử lý tốt mọi tác vụ, từ các tác vụ cơ bản như lướt web</figcaption></figure><p>Với những thông số kỹ thuật trên, iPad Pro 12.9 inch 2022 M2 Wifi 256GB có thể xử lý tốt mọi tác vụ, từ các tác vụ cơ bản như lướt web, xem phim, nghe nhạc, đến các tác vụ chuyên sâu như chỉnh sửa video, chỉnh sửa hình ảnh chuyên nghiệp và chơi game mượt mà.</p><p>iPad Pro 12.9 inch 2022 M2 Wifi 256GB đủ đáp ứng nhu cầu lưu trữ của hầu hết người dùng, cho phép bạn lưu trữ thoải mái các ứng dụng, tài liệu, video, ảnh,...</p><h2><strong>Camera chất lượng cao</strong></h2><p>iPad Pro 12.9 inch 2022 M2 Wifi 256GB được trang bị hệ thống camera sau kép gồm camera góc rộng 12MP và camera góc siêu rộng 10MP. Hệ thống camera này cho khả năng chụp ảnh, quay video chất lượng cao, đáp ứng tốt nhu cầu sử dụng của người dùng.</p><p>Camera trước của iPad Pro 12.9 inch 2022 M2 Wifi 256GB có độ phân giải 12MP, hỗ trợ tính năng Center Stage, giúp bạn luôn nằm trong khung hình khi gọi video.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2024/6/12/2fc79eaf-5442-418d-a64b-1fece29ccc05/image.png" alt=" iPad Pro 12.9 inch 2022 M2 Wifi 256GB có độ phân giải 12MP, hỗ trợ tính năng Center Stage"><figcaption>&nbsp;iPad Pro 12.9 inch 2022 M2 Wifi 256GB có độ phân giải 12MP, hỗ trợ tính năng Center Stage</figcaption></figure><h2><strong>Kết nối đa dạng</strong></h2><p>iPad Pro 12.9 inch 2022 M2 Wifi 256GB có cổng kết nối USB-C hỗ trợ Thunderbolt 4, giúp bạn dễ dàng kết nối với các thiết bị ngoại vi như màn hình, ổ cứng,...</p><h2><strong>Dung lượng pin lớn</strong></h2><p>iPad Pro 12.9 inch 2022 M2 Wifi 256GB có dung lượng pin 40.88 Wh, cho thời gian sử dụng lên tới 10 giờ. Bạn có thể thoải mái sử dụng iPad Pro 12.9 inch 2022 M2 Wifi 256GB cả ngày dài mà không lo hết pin.</p><h2><strong>Tại sao bạn nên mua iPad Pro 12.9 inch 2022 M2 Wifi 256GB tại Phong Vũ?</strong></h2><p>Là sự kết hợp giữa thiết kế đẳng cấp và hiệu suất mạnh mẽ, iPad Pro 12.9 inch 2022 M2 Wifi 256GB chính là một công cụ sáng tạo đa năng, đồng hành đắc lực cho mọi người trong công việc và giải trí hàng ngày.</p><p>Khi bạn quyết định chọn mua iPad Pro 12.9 inch 2022 M2 Wifi 256GB tại Phong Vũ, chúng tôi không chỉ cam kết cung cấp sản phẩm chính hãng mài đội ngũ nhân viên chuyên nghiệp sẽ hỗ trợ bạn trong quá trình chọn mua và hậu mãi. Đặc biệt, với các chương trình khuyến mãi và dịch vụ hỗ trợ, Phong Vũ là địa chỉ đáng tin cậy để sở hữu chiếc iPad Pro 12.9 inch 2022 M2 Wifi 256GB của bạn.</p>',
    1, 'https://lh3.googleusercontent.com/oHOjjQm-OjPX-gg-QvzK2GCyYk7NrkhjaNmT5sKKeobhrmir5MzYR6ginRI9r3qdTkmDvSULilJ2b4XbsApC5Kbe7l_-yptLZg');

