INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url)
VALUES ('iPad Air 5 M1 10.9 inch  5G - 256GB ', 'may-tinh-bang-apple-ipad-air-5-2021-10-9-wifi-cellular-256gb-mmed3za-a-purple-purple--p620945', 2,
    '{"Thương hiệu": "Apple", "Bảo hành": "12 tháng", "Tên": "iPad Air 5 (2021) Wifi + Cellular (MMED3ZA/A)", "Series": "iPad Air", "Màu sắc": "Tím", "Màn hình": "", "Loại màn hình": "10.9\" Liquid Retina IPS", "Độ phân giải": "2360 x 1640 px", "Cấu hình": "", "Dung lượng (ROM)": "5G - 256GB", "Hệ điều hành": "iPadOS", "RAM": "8GB", "Chip": "Apple M1", "Cổng sạc": "USB Type-C", "Loại sim": "eSIM<br>Nano‑SIM (supports Apple SIM)", "Mạng di động": "5G Cellular", "Camera": "", "Camera sau": "12MP", "Camera trước": "12MP", "Kết nối": "", "GPS": "GPS/GNSS<br>Cellular", "Bluetooth": "Bluetooth 5.0", "Thiết kế & trọng lượng": "", "Kích thước": "24.76 x 17.85 x 0.61 cm", "Khối lượng": "462g", "Thông tin kích thước": ""}',
    '<p><a href="https://phongvu.vn/may-tinh-bang-scat.05-N002">Máy tính bảng</a> Apple iPad Air 5 (2022) 10.9 inch Wifi + Cellular 256GB (MMED3ZA/A) (Purple) mang đến làn gió mới cho các iFan, với cấu hình mạnh mẽ cho các tác vụ mượt mà, tính năng phong phú sẵn sàng để đáp ứng tốt cho cả nhu cầu công việc lẫn giải trí của bạn.&nbsp;</p><h2><strong>Tạo hình gọn nhẹ cao cấp, thiết kế màu tím trang nhã&nbsp;</strong></h2><p>Apple iPad Air 5 (2022) <strong>(</strong>MMED3ZA/A) sở hữu kiểu dáng di động linh hoạt với kích thước 24.76 x 17.85 x 0.61 cm và khối lượng 462 g. Chiếc iPad sẵn sàng để theo chân người dùng đến bất kỳ đâu mà không lo cồng kềnh nhờ tạo hình đảm bảo gọn nhẹ, dễ dàng cầm nắm và di chuyển.&nbsp;&nbsp;</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/6/6/20220606_2ea94413-f790-4ae5-8904-04683893cb17.jpg"></figure><p>Phiên bản iPad thế hệ mới khoác lên vẻ đẹp thanh lịch nhờ các đường nét chú trọng sự tinh tế, bốn góc máy bo tròn tạo nên sự thanh thoát. Tông màu tím chủ đạo mang đến cảm giác vừa trang nhã vừa sang trọng. Mặt lưng được cấu tạo từ chất liệu cao cấp hạn chế bám bẩn, dấu vân tay tốt.&nbsp;</p><h2><strong>Màn hình 10.9 inch Liquid Retina sắc nét, hiển thị nội dung sống động</strong></h2><p>Apple iPad Air 5 (2022) mang đến cho người xem không gian hình ảnh mở rộng, nâng cao cảm giác đắm chìm với màn hình Liquid Retina 10.9 inch sắc nét. Thiết kế viền mỏng giúp bạn thoải mái quan sát với tầm nhìn tối ưu hơn, đồng thời đảm bảo vẻ đẹp hoàn thiện cho chiếc iPad.&nbsp;</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/6/6/20220606_e061638d-a3b3-4c4b-b981-8e924bee7f2d.jpg"></figure><p>Màn hình của iPad Air 5 được trang bị tấm nền IPS hiện đại kết hợp độ phân giải 2360 x 1640 px, cung cấp chất lượng khung hình sống động và chân thực hơn bao giờ hết. Mang cả thế giới giải trí tuyệt vời với màu sắc rực rỡ, để bạn có trải nghiệm xem mãn nhãn mọi lúc mọi nơi.&nbsp;</p><h2><strong>Hiệu năng vượt trội với chip Apple M1, đa nhiệm hiệu quả với RAM 8GB</strong></h2><p>Apple iPad Air 5 (2022) <strong>(</strong>MMED3ZA/A) đẩy mạnh khả năng xử lý nhờ tích hợp chip M1 độc quyền của Apple. Với CPU 8 nhân cung cấp hiệu suất mạnh mẽ với tốc độ nhanh hơn 60% so với thế hệ trước, chiếc iPad này đáp ứng tốt nhu cầu chơi game, sáng tạo với trải nghiệm siêu mượt.&nbsp;&nbsp;</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/6/6/20220606_4dc66cf9-31e4-4661-a90e-faa684d913c3.jpg"></figure><p>Với bộ nhớ RAM 8GB, Apple iPad Air 5 có khả năng chạy đa nhiệm hiệu quả cùng lúc nhiều tác vụ, cho phép người dùng thoải mái chơi các game yêu cầu đồ họa chuyên sâu hay khởi chạy nhiều ứng dụng, chuyển đổi linh hoạt các tab mà không lo xảy ra giật lag khó chịu.&nbsp;</p><h2><strong>Bộ nhớ lưu trữ lớn 256GB, khả năng chụp ảnh sắc nét&nbsp;</strong></h2><p>Apple iPad Air 5 (2022) <strong>(</strong>MMED3ZA/A) mang đến không gian lưu trữ mở rộng với bộ nhớ trong có dung lượng lên đến 256GB. Nhờ đó, bạn có thể lưu trữ hình ảnh, video chất lượng cao hoặc các tài liệu quan trọng thoải mái, tải về nhiều ứng dụng, trò chơi yêu thích để trải nghiệm mà không phải lo báo đầy bộ nhớ quá nhanh hay xóa bớt các app.&nbsp;</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/6/6/20220606_73efd498-11d3-4439-bc7e-0c4e4512dcf2.jpg"></figure><p>Với camera sau 12MP và camera trước 12MP, Apple iPad Air 5 giúp người dùng dễ dàng chụp ảnh, quay video bất kỳ lúc nào với chất lượng chân thực. Lưu giữ mọi khoảnh khắc bên bạn bè, người thân hay thỏa mãn đam mê nhiếp ảnh di động với độ phân giải cao và các tính năng tiện ích.&nbsp;</p><h2><strong>Hỗ trợ kết nối tốc độ cao ổn định, cài đặt hệ điều hành iPadOS&nbsp;</strong></h2><p>iPad Air 5 hỗ trợ chơi trò chơi, phát trực tuyến hay tải tệp xuống chỉ trong tích tắc nhờ mạng không dây 5G. Trải nghiệm kết nối mạng gần như không có độ trễ cho nhu cầu xem phim, chơi game trực tuyến thoải mái. Bluetooth 5.0 tốc độ cao, đường truyền ổn định giúp chia sẻ dữ liệu nhanh.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/6/6/20220606_5775db10-d870-4163-be31-efa66b6b9b60.jpg"></figure><p>Hệ điều hành iPadOS được thiết kế thân thiện và theo tính linh hoạt của chiếc iPad Air 5. Hỗ trợ người dùng làm được rất nhiều việc một cách dễ dàng, giúp bạn hoàn thành công việc, tìm kiếm cũng như sắp xếp thông tin thuận tiện, đồng thời giúp chiếc iPad có khả năng hoạt động tốt hơn.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/6/6/20220606_95ad6ce3-53ce-41d9-a235-976735110c7c.jpg"></figure><h2><strong>Video giới thiệu máy tính bảng Apple iPad Air 5</strong></h2><figure class="media"><div data-oembed-url="https://youtu.be/epYaGw4ydoI?si=nQ14IrsslTy-DyJA"><div style="position: relative; padding-bottom: 100%; height: 0; padding-bottom: 56.2493%;"><iframe src="https://www.youtube.com/embed/epYaGw4ydoI" style="position: absolute; width: 100%; height: 100%; top: 0; left: 0;" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen=""></iframe></div></div></figure><h2><strong>Mua máy tính bảng Apple iPad Air 5 (2022) 10.9 inch Wifi + Cellular 256GB (MMED3ZA/A) (Purple) chính hãng tại showroom Phong Vũ | Hỗ trợ bảo hành 12 tháng</strong></h2><p>Apple iPad Air 5 (2022) 10.9 inch Wifi + Cellular 256GB (MMED3ZA/A) (Purple) là sự kết hợp hoàn hảo giữa thiết kế sang trọng cùng hiệu năng đỉnh cao, mang đến cho bạn trải nghiệm giải trí tối ưu. <a href="https://phongvu.vn/contact"><strong>Phong Vũ</strong></a> là địa chỉ mua sắm uy tín chuyên phân phối các sản phẩm điện tử Apple với mức giá hấp dẫn, kèm hỗ trợ bảo hành uy tín trong 1 năm nên đừng bỏ lỡ nhé!&nbsp;</p>',
    1, 'https://lh3.googleusercontent.com/YPDNitWqRUjefX8qnhC_-QGDx4WSVqrlbkGv0so7rQtAXUgHnXXjpX3PfaOKYqc10qvCWO9oDpeTYkKx6w6iD08y1KVKcZvr');

