INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url)
VALUES ('Máy tính bảng Samsung Galaxy Tab A8 2022', 'samsung-galaxy-tab-a8-2022--p409264', 16,
    '{"Thương hiệu": "Samsung", "Bảo hành": "12 tháng", "Tên": "Galaxy Tab A8 2022", "Màu sắc": " Xám - Gray ", "Màn hình": "", "Loại màn hình": "10.5\" TFT LCD", "Độ phân giải": "1920 x 1200", "Cấu hình": "", "Dung lượng (ROM)": "64GB", "RAM": "4GB", "Chip": "Unisoc T618", "Pin": "7,040mAh", "Công nghệ pin": "Sạc nhanh (15W)", "Camera": "", "Kết nối": "", "Thiết kế & trọng lượng": "", "Khối lượng": "476g", "Thông tin kích thước": ""}',
    '<h3><strong>Máy tính bảng Samsung Galaxy Tab A8 2022 (Xám) vừa mới được hãng Samsung ra mắt thị trường với kiểu dáng sang trọng, ram 4GB cùng các công nghệ tiên tiến hiện đại. Sản phẩm hoàn toàn sẽ mang đến cho người dùng những trải nghiệm thư giãn thoải mái và thú vị trong quá trình sử dụng.</strong></h3><h4><strong>Thiết kế bắt mắt, thành phần cấu tạo cao cấp</strong></h4><p>Samsung Galaxy Tab A8 sở hữu vẻ ngoài thời trang với màn hình lớn 10.5inch có tỉ lệ 16:10 cùng tông màu xám chủ đạo. Trọng lượng nhẹ chỉ 476g cho phép người dùng cầm trên tay trong thời gian dài mà không bị mỏi tay hay có thể mang theo khi đi ra ngoài để sử dụng ở bất cứ nơi đâu.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/2/15/20220215_62ab54f9-affd-40df-9083-3e3da1d158ba.jpg" alt="Máy tính bảng Samsung Galaxy Tab A8 2022| Thiết kế thời trang"></figure><p>Hơn nữa, toàn bộ chiếc máy tính bảng được thiết kế từ kim loại nguyên khối. Chất liệu cao cấp, bền bỉ giúp bảo vệ tối đa các linh kiện bên trong trước những tình huống không mong muốn như va đập hay rơi rớt.&nbsp;</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/2/15/20220215_10830c0d-0247-4446-90f8-f19aa6f6c024.jpg" alt="Máy tính bảng Samsung Galaxy Tab A8 2022| Chất liệu cao cấp"></figure><p><strong>Cấu hình vượt trội cùng đa dạng tính năng tiện ích</strong></p><p>Nhà sản xuất đã tích hợp cho chiếc máy tính bảng của mình con chip Unisoc T618 mạnh mẽ cùng bộ nhớ ram 4GB. Đem đến khả năng đa nhiệm cũng như xử lý nhanh chóng các tác vụ thao tác trên sản phẩm của người dùng một cách nhanh chóng và mượt mà.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/2/15/20220215_874ee655-92de-4ed7-b8ba-a57c0509fb1d.jpg"></figure><p>Trải nghiệm âm thanh sống động cực đã trên Galaxy Tab A8&nbsp; nhờ vào hệ thống 4 loa hỗ trợ công nghệ Dolby Atmos. Kèm theo đó là tính năng chia đôi màn hình, thao tác tiếp nối, chuyển đổi thông minh - Auto Switch cùng trình ghi màn hình - Screen Recorder giúp ghi lại khuôn mặt và giọng nói khi đang ghi chú.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/2/15/20220215_05567bc1-aa2e-4542-9de7-e773f3968a99.jpg" alt="Máy tính bảng Samsung Galaxy Tab A8 2022| Đa dạng tính năng tiện ích"></figure><p>Ngoài ra, máy tính bảng Galaxy Tab A8 còn được trang bị dung lượng pin 7,040mAh, cho người dùng thời gian sử dụng lâu dài. Đồng thời, công nghệ sạc nhanh 15W sẽ hỗ trợ sạc đầy nhanh chóng để có thể tiếp tục sử dụng lại.</p><h4><strong>Mua Máy tính bảng Samsung Galaxy Tab A8 2022 chính hãng tại Phong Vũ</strong></h4><p>Hiện nay, chiếc máy tính bảng thời thượng này đã được phân phối chính hãng tại hệ thống showroom của <a href="https://phongvu.vn/"><strong>Phong Vũ</strong></a> trên toàn quốc. Hãy nhanh tay đặt hàng hoặc đến ngay cửa hàng gần nhất trải nghiệm và sở hữu ngay sản phẩm cùng nhiều chương trình ưu đãi hấp dẫn như&nbsp; giảm giá khi thanh toán qua VNPAY-QR…</p>',
    1, 'https://lh3.googleusercontent.com/mL7rqStFFUt2yZXDt1nIQIpZoHtqWQlaKBUPrNuPcRXj1-yokrJ7kdWFT2dzQQpfDgvR57bpwD3CviPFYNj3geoX_CHMZmFf2w');

