INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url)
VALUES ('iPad Mini 6 (A15) 8.3 inch  64GB Wifi ', 'may-tinh-bang-apple-ipad-mini-6-2021-8-3inch-wifi-64gb-mlwl3za-a-pink-pink--p539312', 2,
    '{"Thương hiệu": "Apple", "Bảo hành": "12 tháng", "Tên": "Apple Ipad Mini 6 (2021) 8.3inch Wifi 64GB (MLWL3ZA/A)", "Series": "iPad Mini", "Màu sắc": "Hồng", "Màn hình": "", "Loại màn hình": "8.3\" Liquid Retina IPS", "Độ phân giải": "2266 x 1488 px", "Cấu hình": "", "Dung lượng (ROM)": "64GB", "Hệ điều hành": "iPadOS", "Chip": "Apple A15 Bionic", "Pin": "Lithium‑polymer", "Cổng sạc": "USB Type-C", "Camera": "", "Camera sau": "12MP", "Camera trước": "12MP", "Kết nối": "", "Wifi": "Wifi", "Bluetooth": "Bluetooth 5.0", "Thiết kế & trọng lượng": "", "Kích thước": "19.54 x 13.48 x 0.63 cm", "Khối lượng": "293g", "Thông tin kích thước": ""}',
    '<h2>Máy tính bảng Apple Ipad Mini 6 (2021) 8.3inch Wifi 64GB (MLWL3ZA/A) (Pink) ấn tượng với thiết kế hiện đại, con chip mạnh mẽ Apple A15 Bionic và hệ thống camera sắc nét, chân thật. Chiếc máy tính bảng hoàn toàn chinh phục mọi người dùng trong cả công việc lẫn giải trí thường ngày như xem phim, call video…</h2><h4><strong>Thiết kế sang trọng, bắt mắt</strong></h4><p>Máy tính bảng Apple được thiết kế với kích thước 19.54 x 13.48 x 0.63 cm và khối lượng nhẹ chỉ 293g. Sự gọn nhẹ này đã mang đến cho người dùng sự tiện lợi khi có thể dễ dàng mang theo để sử dụng khi đi ra ngoài, đồng thời không gây mỏi tay khi phải cầm chiếc ipad quá lâu.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/7/1/20220701_c5b9b9d1-749d-4b40-94dc-3240f98764f4.jpg" alt="Máy tính bảng Apple Ipad Mini 6 (2021) 8.3inch Wifi 64GB | Thiết kế sang trọng, bắt mắt"></figure><p>Phần vỏ ngoài được cấu tạo bằng kim loại sáng bóng với gam màu hồng nhẹ nhàng, thời trang. Không những giúp bảo vệ máy và các linh kiện bên trong máy một cách an toàn mà còn góp phần tăng thêm tính thời thượng, sang trọng cho chiếc máy tính bảng này.</p><h4><strong>Màn hình 8.3 inch IPS, Liquid Retina display sắc nét</strong></h4><p>Chiếc màn hình 8.3 inch IPS đã được hãng Apple trang bị cho Ipad Mini 6 (2021) của mình, giúp khả năng hiển thị được nhiều ứng dụng cùng lúc được tốt hơn. Kèm theo đó là độ phân giải 2266 x 1488 pixel mang đến cho người dùng những hình ảnh, video sắc nét và chân thật.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/7/1/20220701_49b43bcc-f285-4e39-a4b8-2e60328ec1d6.jpg" alt="Máy tính bảng Apple Ipad Mini 6 (2021) 8.3inch Wifi 64GB | Màn hình 8.3 inch IPS"></figure><p>Kèm theo đó là công nghệ Liquid Retina display hiện đại tích hợp trên màn hình của chiếc Ipad, cho dải màu rộng cùng khả năng quản lý các kênh màu sắc tốt. Từ đó đem đến cho người dùng những bức ảnh chi tiết, tái hiện một cách sống động và chất lượng hơn bao giờ hết.</p><h4><strong>Hiệu năng vượt trội với con chip Apple A15 Bionic và dung lượng 64GB</strong></h4><p>Ipad Mini 6 (2021) sở hữu con chip Apple A15 Bionic tiên tiến, cho hiệu năng hoạt động mạnh mẽ. Cho người dùng trải nghiệm sự mượt mà và nhanh chóng khi thao tác làm việc hay giải trí với các tác vụ từ cơ bản đến nâng cao một cách dễ dàng.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/7/1/20220701_ce80ff9c-976c-409a-89ca-67e3081b2089.jpg" alt="Máy tính bảng Apple Ipad Mini 6 (2021) 8.3inch Wifi 64GB | Chip Apple A15 Bionic"></figure><p>Với dung lượng lưu trữ lên đến 64GB, chiếc Ipad cho phép lưu trữ được khá nhiều dữ liệu, hình ảnh quan trọng một cách thoải mái. Các bộ phim yêu thích hay những khoảnh khắc kỷ niệm sẽ được lưu lại trong chiếc máy tính bảng này mà không sợ bộ nhớ nhanh bị đầy.</p><h4><strong>Hệ thống camera ấn tượng, chuẩn kết nối hiện đại cùng viên pin Lithium‑polymer</strong></h4><p>Máy tính bảng Apple sở hữu chiếc camera sau và camera trước với độ phân giải 12MP mang đến hình ảnh chất lượng cao. Hỗ trợ tối đa cho trong quá trình làm việc, học online hay tận hưởng những phút giây trò chuyện thư giãn cùng bạn bè và người thân với hình ảnh hiển thị chân thật, sống động.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/7/1/20220701_13b13630-1237-45d3-8fb4-450f3ff4412d.jpg" alt="Máy tính bảng Apple Ipad Mini 6 (2021) 8.3inch Wifi 64GB | Hệ thống camera ấn tượng"></figure><p>Người dùng có thể kết nối internet trên chiếc máy tính bảng Apple một cách dễ dàng nhờ vào Wifi 802.11ax (Wi‑Fi 6) với tốc độ vượt trội. Kèm theo đó là công nghệ truyền phát Bluetooth 5.0 cho khả năng tương thích nhanh chóng với nhiều thiết bị ngoại vi khác.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/7/1/20220701_14f2878e-a606-4669-bf37-ae28483423b2.jpg" alt="Máy tính bảng Apple Ipad Mini 6 (2021) 8.3inch Wifi 64GB | Wifi 802.11ax"></figure><p>Ngoài ra, pin Lithium‑polymer tích hợp trên Ipad Mini 6 còn cho người dùng thời gian sử dụng khá lâu. Cổng sạc USB Type-C tiện ích sạc đầy pin nhanh chóng để người dùng có thể tiếp tục công việc của mình.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/7/1/20220701_f5d968f9-b0c8-438b-8d01-f60ca7c1e571.jpg" alt="Máy tính bảng Apple Ipad Mini 6 (2021) 8.3inch Wifi 64GB | Cổng sạc USB Type-C"></figure><h4><strong>Mua ngay máy tính bảng Apple Ipad Mini 6 (2021) chính hãng tại Phong Vũ</strong></h4><p>Chiếc máy tính bảng thời trang hội tụ đầy đủ những hiệu năng hoạt động mạnh mẽ cùng nhiều tính năng hữu ích, hứa hẹn là sản phẩm lý tưởng đáng để bạn lựa chọn. Hãy đến <a href="https://phongvu.vn/">Phong Vũ</a> trải nghiệm và sở hữu ngay chiếc Ipad này để nhận được nhiều chương trình ưu đãi hấp dẫn nhé!</p>',
    1, 'https://lh3.googleusercontent.com/o7D6rGNmLFoDV7dsccuDtehcSyZoK2AEcVoumF2WYOWs4Iy_HWdEEnUHTJXvD3UpJBk5u3BHFXpQbTYodhdkSWkm_LaUaYkz');

