INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url)
VALUES ('Máy tính bảng Samsung Galaxy Tab A7 Lite 32GB', 'samsung-galaxy-tab-a7-lite-32gb--p254783', 16,
    '{"Thương hiệu": "Samsung", "Bảo hành": "12 tháng", "Tên": "Galaxy Tab A7 Lite", "Màu sắc": " Xám - Gray ", "Màn hình": "", "Loại màn hình": "8.7\" TFT LCD", "Độ phân giải": "1340 x 800 (WXGA+)", "Cấu hình": "", "Dung lượng (ROM)": "32GB", "Hệ điều hành": "Android", "RAM": "3GB", "Chip": "MediaTek MT8768T", "Pin": "5100 mAh", "Công nghệ pin": "Sạc pin nhanhTiết kiệm pin", "Cổng sạc": "USB Type-C", "Loại sim": "1 Nano SIM", "Mạng di động": "Hỗ trợ 4G", "Camera": "", "Camera sau": "8MP", "Camera trước": "2MP", "Kết nối": "", "Wifi": "802.11 a/b/g/n/ac 2.4G+5GHz, VHT80", "GPS": "GPS, Glonass, Beidou, Galileo", "Bluetooth": "Bluetooth 5.0", "Jack tai nghe": "3.5mm", "Kết nối khác": "OTG", "Thiết kế & trọng lượng": "", "Kích thước": "212.5 x 124.7 x 8.0 mm", "Khối lượng": "371 g", "Thông tin kích thước": ""}',
    '<h2><a href="https://phongvu.vn/may-tinh-bang-scat.05-N002"><strong>Máy tính bảng</strong></a><strong> Samsung Galaxy Tab A7 Lite 32GB (SM-T225) (Gray) nổi bật với kiểu dáng nhỏ gọn sang trọng, hiệu năng vừa tầm với những ai không có yêu cầu quá lớn về cấu hình cao. Đây là chiếc tablet chất lượng đáng để bạn trải nghiệm với mức giá khá tốt.&nbsp;</strong></h2><h3><strong>Thiết kế màn hình sống động, cấu hình trải nghiệm ổn định&nbsp;</strong></h3><p>Galaxy Tab A7 Lite được trang bị màn hình 8.7 inch, TFT LCD kèm khung viền được làm mỏng, tối ưu không gian hiển thị để bạn nâng cao chất lượng làm việc và giải trí. Độ phân giải 1340 x 800P (WXGA+) cho phép bạn trải nghiệm không gian hình ảnh sắc nét với chi tiết chân thực.&nbsp;</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/3/16/20220316_7748c066-35f0-4ab0-8d8b-ca59f84b9130.jpg" alt="Máy tính bảng Samsung Galaxy Tab A7 Lite 32GB| Thiết kế màn hình sống động "></figure><p>Máy tính bảng Samsung hoạt động ổn định với bộ nhớ trong 32GB, mang đến không gian lưu trữ vừa phải. Đồng thời RAM 3GB cho phép chiếc máy tính có khả năng đa nhiệm khá tốt nhiều tác vụ, đảm bảo người dùng có trải nghiệm ổn định khi làm việc hay giải trí.&nbsp;</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/3/16/20220316_8c559ea3-c5d6-449b-8713-9fe5d443dbac.jpg" alt="Máy tính bảng Samsung Galaxy Tab A7 Lite 32GB| Cấu hình trải nghiệm ổn đinh "></figure><p><strong>Thời lượng pin ấn tượng, khả năng kết nối tiện ích&nbsp;</strong></p><p>Samsung Galaxy Tab A7 Lite hoạt động với viên pin có thời lượng lớn 5100 mAh. Cùng với đó là công nghệ sạc pin nhanh, giúp bạn tiết kiệm đáng kể thời gian sạc và chế độ tiết kiệm pin để người dùng có thể kéo dài thời gian sử dụng thiết bị lâu hơn.&nbsp;</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/3/16/20220316_0729662e-7219-42d9-9fd3-2bbaf5f2c17a.jpg" alt="Máy tính bảng Samsung Galaxy Tab A7 Lite 32GB| Thời lượng pin ấn tượng "></figure><p>Ngoài hai camera 8MP và 2MP, chiếc máy tính bảng Samsung còn được hỗ trợ kết nối Wifi tốc độ cao, cùng Bluetooth 5.0 đảm bảo tốc độ đường truyền ổn định, với khả năng truy cập nhanh chóng hiệu quả, cho phép bạn thoải mái kết nối và chia sẻ thông tin dù ở bất kỳ đâu.&nbsp;</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2022/3/16/20220316_3c59ae28-27b1-46e0-9fa9-a399d4c60b8b.jpg" alt="Máy tính bảng Samsung Galaxy Tab A7 Lite 32GB| khả năng kết nối tiện ích"></figure><p><strong>Mua máy tính bảng Samsung Galaxy Tab A7 Lite 32GB (SM-T225) (Gray) chính hãng tại showroom Phong Vũ | Hỗ trợ bảo hành 12 tháng&nbsp;</strong></p><p>Samsung Galaxy Tab A7 Lite 32GB là dòng tablet ở phân khúc giá rẻ, phù hợp với nhiều người dùng. Hiện chiếc máy tính bảng Samsung đang được phân phối tại showroom <a href="https://phongvu.vn/"><strong>Phong Vũ</strong></a> kèm bảo hành 1 năm uy tín. Nếu có nhu cầu mua sản phẩm, bạn có thể ghé ngay showroom hoặc đặt hàng qua website để được giao hàng đến tận nơi hoàn toàn miễn phí nhé!&nbsp;</p>',
