INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url)
VALUES ('Điện thoại Samsung Galaxy S23 Ultra', 'samsung-galaxy-s23-ultra--p500205', 16,
    '{"Thương hiệu": "Samsung", "Bảo hành": "12 tháng", "Tên": "Galaxy S23 Ultra", "Series": "Galaxy S", "Màu sắc": " Xanh - Botanic Green ", "Màn hình": "", "Loại màn hình": "6.8\" Dynamic AMOLED 2X", "Độ phân giải": "Quad HD+ (2K+) (3088 x 1440)", "Cấu hình": "", "Dung lượng (ROM)": "256GB", "Hệ điều hành": "Android", "RAM": "8GB", "Chip": "Snapdragon 8 Gen 2", "Pin": "5000 mAh", "Công nghệ pin": "Sạc pin nhanh; Sạc ngược không dây; Sạc không dây", "Cổng sạc": "USB Type-C", "Mạng di động": "Hỗ trợ 5G", "Camera": "", "Camera sau": "2 x 10MP, 200MP, 12 MP", "Camera trước": "12 MP", "Kết nối": "", "Wifi": "Wi-Fi Direct; Dual-band (2.4 GHz/5 GHz); Wi-Fi 802.11 a/b/g/n/ac/ax; 6 GHz", "GPS": "GALILEO; BEIDOU; GLONASS; GPS", "Bluetooth": "Bluetooth 5.3", "Jack tai nghe": "USB Type-C", "Kết nối khác": "GSM / CDMA / HSPA / EVDO / LTE", "Thiết kế & trọng lượng": "", "Kích thước": "78.1 x 163.4 x 8.9 mm", "Khối lượng": "233g", "Thông tin kích thước": ""}',
    '<p><a href="https://phongvu.vn/samsung-galaxy-s23-ultra--p500205"><i><strong>Samsung S23 Ultra</strong></i></a><i><strong> là một trong những sản phẩm smartphone cao cấp đến từ nhà Samsung mang vẻ ngoài sang trọng, camera tuyệt đỉnh. Với đầy đủ tính năng mới, hơn thế nữa là chất liệu bền vững kết hợp với thiết kế tối giản và tối ưu hóa năng lượng là những yếu tố giúp S23 Ultra không chỉ là một chiếc điện thoại mà còn trở thành một sản phẩm tốt cho người tiêu dùng. Hãy cùng tìm hiểu thêm về những tính năng của chiếc điện thoại siêu phẩm nhà Samsung này trong bài viết dưới đây nhé!</strong></i></p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2023/2/22/20230222_7dcd1459-da0b-4f58-bf8e-75ea133b6902.jpg"></figure><h2><strong>Thiết kế - ĐỘT PHÁ HƠN, BỀN VỮNG HƠN</strong></h2><p>Thiết kế đối xứng đậm chất Ultra trở lại và khẳng định mạnh mẽ hơn nữa cho công cuộc theo đuổi sự bền vững: Chất liệu tái chế với mong muốn bảo vệ môi trường. Từ lớp kính phủ là vật liệu tái chế đến màu sơn độc bản của khung viền kim loại, vẻ đẹp là bản sắc vốn có mang tên Samsung Galaxy S23 Ultra.</p><h3>Màu sắc - Sắc màu tinh tế từ thiên nhiên</h3><ul><li>Thiết kế thanh lịch</li></ul><p>Thanh lịch và tinh tế, bộ sưu tập màu sắc với chất liệu thiên nhiên thổi hồn vào từng siêu phẩm, mang đến một nét đẹp không thể từ chối mà chẳng hề phô trương</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2023/2/22/20230222_a51be3e2-55af-4e4b-8c06-2e2677a5ee87.png"></figure><h3>Màu sắc độc quyền:&nbsp;</h3><p>Không ngại ghi dấu ấn với lối sống xanh và tinh thần yêu thiên nhiên? Những gam màu này là dành cho bạn dễ dàng lựa chọn: Xanh Lime Xanh Sky Blue Xám Graphite Đỏ Lychee</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2023/2/22/20230222_656f154d-766f-4b47-b91f-8514763bad2e.png"></figure><h3>Chất liệu: Thiết kế bền vững. Vì bạn và vì môi trường</h3><p>Bước tiến lớn trong lịch sử thiết kế điện thoại thông minh: Chất liệu tái chế cho một tương lai bền vững hơn hết. Khoác lên Galaxy S23 Ultra là lớp kính tái chế và phim phủ PET không chỉ lộng lẫy ở mọi góc cạnh mà còn giúp siêu phẩm di động nổi bật khác biệt với tuyên ngôn sống xanh đột phá.</p><p>&nbsp;</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2023/2/22/20230222_118dfe4d-5c18-44d2-837f-d0d45227041e.png"></figure><h3>S Pen: Đối tác đắc lực cho việc làm việc và học tập từ xa</h3><p>Siêu bút S Pen cho di sản của Note tồn tại. Thêm vào đó, nó giúp bạn không còn phải phụ thuộc vào sổ tay, tạo ra các bản phác thảo bản ghi nhớ dễ dàng.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2023/2/22/20230222_ca9ef357-798f-4d81-a414-2284523938f2.png"></figure><h3>Trải nghiệm chụp ảnh tuyệt đỉnh với camera</h3><p>Tiếp nối huyền thoại của những chiếc điện thoại Galaxy S,&nbsp; trọn bộ công nghệ đỉnh cao đã được hội tụ trong Camera Mắt Thần Bóng Đêm. Với cảm biến điểm ảnh lớn&nbsp; trên thị trường hiện nay, chiếc Samsung S23 Ultra&nbsp; này cho phép bạn chụp ảnh với độ phân giải cao và chi tiết tuyệt vời.</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2023/2/22/20230222_fca5cc3b-d907-4f9a-8e9e-226a0f5223cf.png"></figure><ul><li>CAMERA MẮT THẦN BÓNG ĐÊM</li></ul><p>Ảnh thiếu sáng đã trở thành ký ức</p><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2023/2/22/20230222_c3bb7487-9f77-4079-ae06-bc50081b56ad.png"></figure><p>&nbsp;</p><ul><li>Chụp chân dung đêm</li></ul><p>Sâu hơn, rộng hơn - đó là trường ảnh thu được từ bộ Camera chuyên nghiệp của Galaxy S23 Ultra. Mỗi khung hình đều được xóa phông tự nhiên, nổi bật chủ thể và vô cùng giàu chi tiết với cấp độ DSLR.</p><ul><li>Chế độ đứng</li></ul><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2023/2/22/20230222_2ce67e8a-3a31-4645-9970-284fc1232d87.png"></figure><ul><li>Chế độ ngang</li></ul><figure class="image"><img src="https://storage.googleapis.com/teko-gae.appspot.com/media/image/2023/2/22/20230222_43a60f9a-ea85-436c-87c2-a76b7447b3ce.png"></figure><p>Trên đây là những thông tin đầy đủ và chính xác về Samsung Galaxy S23 Ultra, một trong những smartphone đáng chú ý trong năm nay với thiết kế đẹp mắt, tính năng vượt trội và đặc biệt là chất lượng camera cực kỳ ấn tượng. Nếu bạn đang tìm kiếm một chiếc điện thoại cao cấp để trải nghiệm công nghệ mới nhất, hãy đến Phong Vũ để sở hữu ngay sản phẩm này với giá tốt trên thị trường và nhiều ưu đãi hấp dẫn. Chúc bạn tìm được sản phẩm ưng ý và có trải nghiệm tuyệt vời cùng Samsung Galaxy S23 Ultra!</p>',
    1, 'https://lh3.googleusercontent.com/9wJ1kIod0RCqmF8uriCIki4n-cpt3ZqYI_Q6YOAJot9_ingCikktEt2VFOAkxk1ssy1YY0vExsj0j4Zl2NjnqRzh0tYbemOy');

