INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Razer BlackShark V2 X For PlayStation', 'tai-nghe-razer-blackshark-v2-x-for-playstation', 15,
        '{"Nhà sản xuất": "Razer", "Loại tai nghe": "Over-Ear", "Chuẩn kết nối": "Audio 3.5mm", "Driver": "50 mm", "Dải tần số": "12 Hz – 28 kHz", "Micro": "HyperClear Cardioid", "Loại pin": "Pin", "Tính năng nổi bật": "Âm thanh vòm 7.1, Thoáng khí, Chống ồn chủ động, Bản dành cho PlayStation có thể dùng cho PC/PS5/Switch/Xbox", "Cân nặng": "240g", "Thời gian bảo hành": "24 tháng (cần giữ lại phụ kiện và hộp sản phẩm)"}',
        'Bước vào thế giới của âm thanh sống động và chiến thắng tuyệt đối với Tai nghe Razer BlackShark V2 X For PlayStation. Được thiết kế đặc biệt để nâng tầm trải nghiệm gaming trên PlayStation của bạn, chiếc tai nghe này mang đến sự kết hợp hoàn hảo giữa chất lượng âm thanh vượt trội, khả năng giao tiếp rõ ràng và sự thoải mái tối đa, giúp bạn thống trị mọi chiến trường ảo.\n\n**Âm Thanh Chính Xác, Lợi Thế Tuyệt Đối:** Trang bị driver Razer™ TriForce 50mm độc quyền, Razer BlackShark V2 X tái tạo âm thanh tách bạch rõ ràng ở các dải âm cao, trung và trầm, giúp bạn nghe rõ từng tiếng bước chân của kẻ địch, tiếng súng nổ vang dội hay lời thoại sắc nét của đồng đội. Đắm chìm hoàn toàn vào thế giới game với âm trường rộng mở và chi tiết đến kinh ngạc, mang lại lợi thế chiến thuật không thể phủ nhận.\n\n**Giao Tiếp Rõ Ràng, Phối Hợp Nhịp Nhàng:** Microphone Razer™ HyperClear Cardioid có thể tháo rời đảm bảo giọng nói của bạn luôn trong trẻo và không bị nhiễu. Với khả năng loại bỏ tiếng ồn xung quanh hiệu quả, đồng đội sẽ chỉ nghe thấy giọng nói của bạn, mang lại lợi thế giao tiếp vượt trội trong mọi trận đấu căng thẳng.\n\n**Thoải Mái Bền Bỉ, Tập Trung Tối Đa:** Với trọng lượng siêu nhẹ chỉ 240g và đệm tai FlowKnit bọc mút hoạt tính thoáng khí, Razer BlackShark V2 X mang lại sự thoải mái vượt trội ngay cả trong những phiên đấu game kéo dài hàng giờ. Thiết kế chụp tai kín cùng khả năng cách âm thụ động tiên tiến giúp bạn tập trung hoàn toàn vào trận đấu, loại bỏ mọi phiền nhiễu từ môi trường bên ngoài.\n\n**Kết Nối Đa Năng, Sẵn Sàng Chinh Phục:** Kết nối analog 3.5mm phổ biến giúp tai nghe tương thích mượt mà với PlayStation, PC, Mac, Nintendo Switch và các thiết bị di động khác. Đơn giản chỉ cần cắm vào và sẵn sàng chinh phục mọi thử thách. Razer BlackShark V2 X For PlayStation không chỉ là một chiếc tai nghe, mà là vũ khí tối thượng giúp bạn nâng tầm trải nghiệm gaming PlayStation của mình lên một tầm cao mới.',
        1, 'https://cdn.hstatic.net/products/200000722513/imgi_219_tai-nghe-gaming-razer-blackshark-v2-x-for-console-03_9d805beea62348fda3028adc3470cc13.jpg'
    );
