INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Razer Barracuda X Chroma Black', 'tai-nghe-razer-barracuda-x-chroma-black', 15,
        '{"Loại tai nghe": "Over-Ear", "Chuẩn kết nối": "Bluetooth, Wireless 2.4GHz, Wired USB-C", "Driver": "40 mm", "Dải tần số": "20 Hz - 20 kHz", "Micro": "Razer HyperClear Cardioid Mic", "Loại pin": "Pin sạc", "Thời lượng pin": "Có đèn nền + 2.4GHz: Lên đến 35 giờ; Không đèn nền + 2.4GHz: Lên đến 70 giờ", "Tính năng nổi bật": "Razer TriForce; 6-Zone Earcup Lighting; 7.1 Surround Sound (chỉ hỗ trợ trên Windows 10 64-bit hoặc cao hơn)", "Phần mềm hỗ trợ": "Razer Synapse (PC); Razer Audio App (Mobile)", "Cân nặng": "285g", "Phụ kiện đi kèm": "Cáp USB-C to USB-C", "Thời gian bảo hành": "24 tháng (giữ lại phụ kiện và hộp sản phẩm)"}',
        'Hãy đắm chìm vào thế giới giải trí không giới hạn với Tai nghe Razer Barracuda X – người bạn đồng hành không dây đa năng, tối ưu hóa cho mọi nền tảng. Với thiết kế màu đen sang trọng và tinh tế, chiếc tai nghe này không chỉ là một phụ kiện gaming mà còn là biểu tượng của sự linh hoạt và hiệu suất đỉnh cao.\n\nĐược chế tạo để mang lại sự thoải mái tối đa, Barracuda X chỉ nặng vỏn vẹn 250g, đảm bảo bạn có thể chơi game, nghe nhạc hay xem phim suốt hàng giờ liền mà không hề cảm thấy khó chịu. Đệm tai bằng mút hoạt tính mềm mại và băng đô nhẹ nhàng sẽ ôm trọn đầu bạn một cách hoàn hảo, cho phép bạn hoàn toàn tập trung vào trải nghiệm của mình.\n\nTrải nghiệm âm thanh tuyệt vời với trình điều khiển Razer TriForce 40mm độc quyền, mang đến âm cao sắc nét, âm trung phong phú và âm trầm mạnh mẽ. Dù bạn đang nghe tiếng bước chân của kẻ thù trong game, thưởng thức bản nhạc yêu thích hay xem một bộ phim hành động gay cấn, mọi âm thanh đều được tái tạo một cách chân thực và sống động nhất.\n\nKhả năng kết nối không dây 4-trong-1 là điểm nhấn của Barracuda X. Với bộ chuyển đổi USB-C tốc độ cao, bạn có thể dễ dàng chuyển đổi liền mạch giữa PC, PlayStation, Nintendo Switch và các thiết bị Android mà không cần dây cáp rườm rà hay thiết lập phức tạp. Chỉ cần cắm vào và tận hưởng trải nghiệm âm thanh không dây mượt mà.\n\nGiao tiếp luôn rõ ràng với micro Razer HyperClear Cardioid có thể tháo rời. Thiết kế chống ồn hiệu quả giúp giọng nói của bạn luôn được truyền tải một cách trong trẻo, không bị nhiễu bởi tiếng ồn xung quanh, đảm bảo đồng đội luôn nghe rõ mọi mệnh lệnh của bạn trong những trận đấu căng thẳng.\n\nVới thời lượng pin ấn tượng lên đến 20 giờ, Razer Barracuda X sẵn sàng đồng hành cùng bạn trong mọi cuộc phiêu lưu, từ những trận đấu game kéo dài đến những chuyến đi xa. Đây chính là sự lựa chọn hoàn hảo cho những game thủ đa nền tảng đang tìm kiếm sự tiện lợi, thoải mái và chất lượng âm thanh vượt trội trong một thiết kế tinh tế, mạnh mẽ.',
        1, 'https://cdn.hstatic.net/products/200000722513/download__4__5e8ed535e8c640bfa3c8529bc5f1476c.png'
    );
