INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe E-Dra EH414W White', 'tai-edra-eh414w-whi', 23,
        '{"Tên sản phẩm": "Tai nghe E-Dra EH414W White", "Màu sắc": "Trắng", "Kiểu kết nối": "Không dây 2.4GHz", "Kích thước Driver": "50mm", "Tần số phản hồi tai nghe": "20Hz - 20kHz", "Độ nhạy tai nghe": "103 ± 3dB", "Trở kháng tai nghe": "32 Ω", "Loại Microphone": "Đa hướng", "Độ nhạy Microphone": "-42 ± 3dB", "Kích thước Microphone": "Φ6.0 x 2.7 mm", "Tần số phản hồi Microphone": "100Hz - 10kHz", "Dung lượng pin": "600 mAh", "Thời lượng pin": "Khoảng 15 giờ", "Cổng sạc": "USB Type-C", "Phạm vi kết nối": "10 mét", "Tương thích": "PC, PS4, PS5, Xbox One, Nintendo Switch, Mobile", "Chất liệu đệm tai": "Da tổng hợp", "Đèn nền": "Không", "Trọng lượng": "Khoảng 320g"}',
        'Tai nghe E-Dra EH414W White là sự kết hợp hoàn hảo giữa hiệu năng âm thanh đỉnh cao, thiết kế thời trang và sự thoải mái vượt trội, mang đến trải nghiệm giải trí và làm việc sống động. Với tông màu trắng tinh khôi, EH414W không chỉ là một thiết bị âm thanh mà còn là một phụ kiện thời trang, dễ dàng phối hợp với mọi không gian và phong cách.\n\nÂm thanh chân thực và sống động là điểm nhấn chính của EH414W. Được trang bị màng loa chất lượng cao, tai nghe tái tạo âm thanh chi tiết với dải tần rộng, cho phép bạn cảm nhận rõ ràng từng nốt nhạc, từng âm bass mạnh mẽ hay những hiệu ứng âm thanh gay cấn trong game. Dù bạn đang thưởng thức âm nhạc yêu thích, xem phim bom tấn hay đắm chìm trong thế giới game, EH414W đều mang đến chất lượng âm thanh ấn tượng, độ rõ nét cao và âm trường rộng mở.\n\nThiết kế của EH414W tập trung vào sự thoải mái cho người dùng, ngay cả khi sử dụng trong thời gian dài. Earcup được bọc đệm da mềm mại, ôm trọn tai giúp cách âm tốt, đồng thời giảm thiểu áp lực lên vành tai. Phần headband có thể điều chỉnh linh hoạt, phù hợp với nhiều kích cỡ đầu khác nhau, đảm bảo sự vừa vặn và ổn định. Chất liệu nhẹ nhàng kết hợp với đệm tai êm ái tạo nên cảm giác đeo dễ chịu, không gây khó chịu hay mỏi tai.\n\nNgoài ra, EH414W còn tích hợp micro chất lượng, giúp bạn giao tiếp rõ ràng trong các cuộc gọi trực tuyến, họp hành hay voice chat khi chơi game. Khả năng lọc tiếng ồn hiệu quả của micro đảm bảo giọng nói của bạn luôn được truyền tải một cách tốt nhất đến người đối diện. Kết nối đơn giản và ổn định, tương thích với nhiều thiết bị khác nhau, EH414W White là lựa chọn lý tưởng cho mọi nhu cầu âm thanh của bạn.',
        1, 'https://cdn.hstatic.net/products/200000722513/download__4__d20722e010b54339bf49427884a67ef9.png'
    );
