INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Không dây Logitech Zone 300 Hồng', 'tai-nghe-khong-day-logitech-zone-300-hong', 13,
        '{"Thời gian sử dụng": "20 giờ", "Thời gian sạc": "Khoảng 2 giờ", "Cổng sạc": "Type-C", "Công nghệ âm thanh": "Driver 30 mm", "Tương thích": "macOS, ChromeOS, PlayStation 4, PlayStation 5, Android, iOS, Windows, iPadOS", "Ứng dụng kết nối": "Logi Tune", "Jack cắm": "3.5 mm", "Độ dài dây": "1 m", "Tiện ích": "Có mic thoại, Sạc nhanh, 2 Micro chống ồn", "Kết nối cùng lúc": "2 thiết bị", "Công nghệ kết nối": "Bluetooth 5.3", "Điều khiển": "Phím nhấn", "Phím điều khiển": "Tăng/giảm âm lượng, Phát/dừng chơi nhạc, Nhận/Ngắt cuộc gọi", "Kích thước": "Dài 17.28 cm - Rộng 16.67 cm - Cao 6.6 cm", "Khối lượng": "122 g", "Thương hiệu": "Logitech", "Sản xuất tại": "Việt Nam"}',
        'Tai nghe chụp tai Logitech Zone 300 sở hữu một thiết kế tối giản cùng trọng lượng nhẹ, đệm mút tai êm ái để mang tới trải nghiệm đeo thoải mái trong thời gian dài. Kèm theo đó là chất lượng âm thanh và khả năng đàm thoại rõ ràng để phục vụ tốt cho đa dạng các nhu cầu làm việc, giải trí của người dùng.\n\nÂm thanh rõ ràng với màng loa 30mm\nMỗi bên tai nghe chụp tai Logitech Zone 300 được trang bị một màng loa 30mm để mang tới âm thanh rõ ràng cho người nghe. Với độ nhạy tần số dao động từ 50Hz tới 20KHz, các dải âm sẽ được xử lý chi tiết để phù hợp với nhiều dòng nhạc phổ biến hiện nay. Không chỉ các bản nhạc, mẫu tai nghe chụp tai của Logitech còn có thể tái tạo tốt giọng nói trong các cuộc gọi để truyền tải chính xác thông tin trong các cuộc đàm thoại.\n\nChất lượng âm thanh của tai nghe chụp tai Logitech Zone 300\n\nTrọng lượng siêu nhẹ, vẻ ngoài tối giản\nLogitech Zone 300 là mẫu tai nghe chụp tai thân thiện với môi trường khi nhiều chi tiết trên sản phẩm được làm từ nhựa tái chế. Kèm theo đó là một thiết kế tối giản cùng các tuỳ chọn màu sắc trẻ trung để phù hợp với đa dạng các phong cách, sở thích của người dùng. Mẫu tai nghe không dây Zone 300 của còn có trọng lượng siêu nhẹ, chỉ khoảng 122gr, để người dùng có thể thoải mái sử dụng cả ngày.\n\nThiết kế tai nghe chụp tai Logitech Zone 300\n\nĐàm thoại hiệu quả với bộ đôi mic khử ồn\nTai nghe chụp tai Logitech Zone 300 được trang bị hệ thống cần micro tích hợp hai micro khử tiếng ồn, giúp giảm thiểu tối đa các tạp âm từ môi trường xung quanh. Thiết kế này không chỉ phù hợp cho các buổi họp online mà còn rất lý tưởng cho việc học tập, làm việc từ xa, hoặc khi sử dụng trong không gian công cộng.\n\nĐồng thời, khả năng khử ồn trên tai nghe cũng giúp cho giọng nói của người dùng được truyền tải một cách rõ ràng và chính xác. Nhờ đó mà các nội dung quan trọng không bị nhiễu loạn bởi âm thanh nền, từ đó nâng cao chất lượng giao tiếp và hiệu quả công việc.\n\nKhả năng đàm thoại của tai nghe chụp tai Logitech Zone 300\n\nHoạt động bền bỉ với viên pin Lithium ion hỗ trợ sạc nhanh\nTai nghe Logitech Zone 300 sử dụng dòng pin Lithium ion có thể sạc lại. Sau mỗi 2 giờ sạc đầy năng lượng, mẫu tai nghe không dây của Logitech có thể cung cấp thời lượng đàm thoại tối đa tới 16 giờ và thời lượng nghe tối đa tới 20 giờ tuỳ vào mức âm lượng được cài đặt. Viên pin Lithium ion được trang bị bên trong Logitech Zone 300 còn hỗ trợ sạc nhanh để có thể tiếp tục đàm thoại tới 1 giờ chỉ với 5 phút sạc.\n\nPin và khả năng sạc của tai nghe chụp tai Logitech Zone 300\n\nKết nối ổn định, tùy chỉnh thông qua ứng dụng\nTai nghe chụp tai Logitech Zone 300 sử dụng công nghệ Bluetooth 5.3 để hỗ trợ quá trình kết nối không dây. Công nghệ Bluetooth này không chỉ mang tới sự ổn định mà còn đồng thời hỗ trợ tay nghe Logitech Zone 300 có thể kết nối đa điểm để sử dụng trên nhiều thiết bị cùng lúc. Đi kèm tai nghe là ứng dụng Logi Tune để giúp những tín đồ âm thanh có thể cá nhân hóa chất âm theo sở thích của bản thân.',
        1, 'https://product.hstatic.net/200000722513/product/sucupuge_392c1218db254d21bc7f90df8421197c.png'
    );
