INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Edifier không dây W830NB Gray', 'tai-nghe-edifier-khong-day-w830nb-gray', 30,
        '{"Đa dạng đầu vào": "Bluetooth / USB Type-C"}',
        'Tai nghe Edifier W830NB Gray là một lựa chọn tuyệt vời cho những ai yêu thích âm nhạc và cần một thiết bị âm thanh không dây chất lượng cao. Với thiết kế over-ear (trùm tai) hiện đại, tai nghe mang lại sự thoải mái tối đa ngay cả khi sử dụng trong thời gian dài. Chất liệu đệm tai mềm mại, êm ái bao bọc vành tai, tạo cảm giác dễ chịu và cách âm hiệu quả. Màu xám trang nhã, tinh tế, phù hợp với nhiều phong cách khác nhau, từ công sở đến dạo phố. Edifier W830NB nổi bật với công nghệ chống ồn chủ động (ANC) tiên tiến, giúp loại bỏ tiếng ồn xung quanh, cho phép bạn đắm chìm hoàn toàn vào thế giới âm nhạc, podcast hoặc cuộc gọi thoại mà không bị làm phiền. Chất lượng âm thanh được tinh chỉnh bởi các kỹ sư âm thanh chuyên nghiệp của Edifier, mang đến âm bass sâu lắng, âm trung rõ ràng và âm cao trong trẻo, tái tạo mọi chi tiết của bản nhạc một cách sống động. Thời lượng pin ấn tượng lên đến 45 giờ sử dụng liên tục với chế độ ANC bật và có thể lên tới 95 giờ khi tắt ANC, giúp bạn thoải mái sử dụng cả tuần mà không cần lo lắng về việc sạc pin. Kết nối Bluetooth 5.0 ổn định, phạm vi kết nối rộng, đảm bảo trải nghiệm nghe nhạc liền mạch. Tai nghe còn được trang bị micro chất lượng cao, hỗ trợ đàm thoại rảnh tay rõ ràng. Nút điều khiển tích hợp trên củ tai giúp bạn dễ dàng chuyển bài, điều chỉnh âm lượng, nhận/từ chối cuộc gọi và kích hoạt trợ lý ảo chỉ với thao tác chạm đơn giản. Edifier W830NB Gray là sự kết hợp hoàn hảo giữa thiết kế đẹp mắt, công nghệ hiện đại và hiệu suất âm thanh đỉnh cao, mang đến trải nghiệm nghe nhìn tuyệt vời cho người dùng.',
        1, 'https://product.hstatic.net/200000722513/product/4862dd949b36b3db8f8f58b12c124bea_a0a3efffd71a4ce7bf14ff32b5f46692.png'
    );
