INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Razer Kraken V3 X USB', 'tai-nghe-razer-kraken-v3-x-usb', 15,
        '{"Thương hiệu:": "Razer", "Model:": "Kraken V3 X USB", "Màu sắc:": "Đen", "Kết nối:": "Có dây", "Kiểu tai nghe": "Over-ear", "Kiểu kết nối:": "Có dây", "Đèn LED:": "RGB", "Chuẩn kết nối:": "USB", "Microphone:": "Có", "Dây:": "Dài 2m", "Trở kháng:": "32 Ω (1 kHz)", "Tần số:": "12 Hz - 28 kHz", "Khả năng cách âm:": "Có", "Chất liệu khung:": "Kim loại", "Chất liệu đệm tai nghe:": "Da", "Tương thích:": "PC/iOS/Laptop"}',
        'Tai nghe Razer Kraken V3 X USB là một lựa chọn tuyệt vời cho game thủ và người dùng yêu cầu chất lượng âm thanh cao. Với thiết kế công thái học, đệm tai bằng vải thoáng khí và phần đệm đầu mềm mại, Kraken V3 X mang đến sự thoải mái tối đa ngay cả trong những phiên chơi game hoặc làm việc kéo dài.\n\nDriver Razer TriForce 50mm độc quyền cho âm thanh sắc nét, mạnh mẽ với âm bass sâu, giúp bạn nghe rõ từng bước chân địch thủ hay cảm nhận trọn vẹn từng hiệu ứng trong game. Micro định hướng HyperClear Cardioid tiên tiến thu âm giọng nói rõ ràng, giảm thiểu tiếng ồn xung quanh, đảm bảo giao tiếp mượt mà với đồng đội.\n\nKết nối USB đơn giản, tương thích với nhiều nền tảng như PC, PlayStation, Nintendo Switch, mang đến sự linh hoạt. Hệ thống đèn Chroma RGB với 4 vùng chiếu sáng, cho phép tùy chỉnh hiệu ứng màu sắc sống động, thể hiện cá tính của bạn.\n\nVỏ ngoài bền bỉ, nhẹ nhàng, với trọng lượng chỉ 285g, không gây áp lực lên đầu. Điều chỉnh âm lượng và tắt tiếng micro dễ dàng ngay trên củ tai. Razer Kraken V3 X USB là sự kết hợp hoàn hảo giữa hiệu năng âm thanh, sự thoải mái và phong cách, là một khoản đầu tư xứng đáng cho trải nghiệm giải trí của bạn.',
        1, 'https://product.hstatic.net/200000722513/product/ezgif-3-2a102e266ba9_15326bef447c473eb07d61b0e843d206_5771f81db9cb441cbc3698ac0e24f888.png'
    );
