INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Asus ROG Cetra II Core Moonlight', 'tai-nghe-asus-rog-cetra-ii-core-moonlight', 3,
        '{"Thương hiệu": "Asus", "Model": "Tai nghe Asus ROG Cetra II Core Moonlight", "Kết nối": "Jack 3.5mm", "Chiều dài cáp": "1,25 m", "Kích thước driver": "9.4mm", "Trở kháng": "32 ohm", "Dạng tín hiệu": "Microphone: Omni-directional", "Phản hồi tần số": "20Hz - 40KHz", "Trọng lượng": "18g", "Phụ kiện": "Hộp đựng, cáp chia audio/mic, 3 cặp vây tai (nhiều kích cỡ), 3 cặp nút tai silicon (nhiều kích cỡ), 1 cặp nút tai foam, Sách hướng dẫn nhanh"}',
        'Asus ROG Cetra II Core Moonlight không chỉ là một chiếc tai nghe, mà là tuyên ngôn phong cách và sức mạnh âm thanh. Với sắc trắng Moonlight độc đáo, thiết kế tinh tế và hiệu suất âm thanh vượt trội, chiếc tai nghe in-ear này được sinh ra để định nghĩa lại trải nghiệm gaming và giải trí của bạn.\n\nĐắm chìm vào thế giới game với chất âm sống động đến kinh ngạc. ROG Cetra II Core Moonlight được trang bị driver ASUS Essence độc quyền với nam châm Neodymium lỏng, mang lại dải âm trầm sâu lắng, mạnh mẽ cùng âm trung và cao trong trẻo, chi tiết. Mọi âm thanh từ tiếng bước chân đối thủ lén lút đến những pha cháy nổ hoành tráng đều được tái tạo chân thực, giúp bạn luôn dẫn trước trong mọi trận đấu.\n\nThiết kế công thái học vượt trội đảm bảo sự thoải mái tối đa ngay cả trong những phiên chơi game kéo dài. Vỏ ngoài bằng nhôm nhẹ và bền bỉ, kết hợp với các eartip và earfin silicone siêu mềm với nhiều kích cỡ, giúp tai nghe vừa vặn hoàn hảo, cách âm thụ động hiệu quả, cho phép bạn tập trung hoàn toàn vào trận chiến mà không bị xao nhãng.\n\nDây cáp bện chất lượng cao không chỉ tăng cường độ bền mà còn chống rối hiệu quả, trong khi cổng kết nối 3.5mm tiêu chuẩn mang lại khả năng tương thích đa nền tảng mạnh mẽ. Dù bạn chơi game trên PC, Mac, PlayStation, Xbox, Nintendo Switch hay thưởng thức âm nhạc trên thiết bị di động, ROG Cetra II Core Moonlight luôn sẵn sàng đồng hành cùng bạn. Microphone tích hợp đảm bảo giọng nói của bạn luôn rõ ràng, mạch lạc khi giao tiếp với đồng đội.\n\nHãy để Asus ROG Cetra II Core Moonlight là vũ khí bí mật giúp bạn thống trị mọi cuộc chơi và tận hưởng từng khoảnh khắc giải trí một cách trọn vẹn nhất.',
        1, 'https://product.hstatic.net/200000722513/product/10249_rog_cetra_ii_core_moonlight_white_2_33df49bdb12244509291cae8c0ecf5a6.jpg'
    );
