INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Asus ROG Cetra True Wireless', 'tai-nghe-asus-rog-cetra-true-wireless', 3,
        '{"Công nghệ âm thanh": "Chống ồn chủ động, Tuỳ chỉnh EQ/âm thanh 7.1", "Micro": "Có", "Tính năng": "Chống ồn, Điều khiển giọng nói, Có mic đàm thoại, Sạc không dây", "Bluetooth": "5.0", "Ứng dụng kết nối": "Armoury Crate", "Dung lượng pin": "Tai nghe: 5.5 giờ, Hộp sạc: 21.5 giờ", "Cổng sạc": "USB-C, Sạc không dây", "Phương thức điều khiển": "Cảm ứng chạm", "Thao tác điều khiển": "Chuyển bài, Điều chỉnh âm lượng, Trả lời cuộc gọi, Kích hoạt trợ lý giọng nói, Chuyển đổi giữa các chế độ khác nhau", "Tính năng khác": "Sạc nhanh", "Trọng lượng": "Tai nghe: 5 g, Hộp sạc: 42 g", "Phạm vi kết nối": "10 m", "Hãng sản xuất": "ASUS"}',
        'Tai nghe Asus ROG Cetra True Wireless mang đến trải nghiệm âm thanh đỉnh cao, kết hợp công nghệ chống ồn chủ động (ANC) tiên tiến để bạn đắm chìm hoàn toàn vào thế giới âm nhạc, game hoặc podcast yêu thích mà không bị làm phiền bởi tiếng ồn xung quanh. Với driver âm thanh 10mm Neodymium, ROG Cetra True Wireless tái tạo âm thanh sống động, mạnh mẽ và chi tiết, đặc biệt là dải bass sâu lắng và âm treble trong trẻo. Thiết kế công thái học cùng với 3 kích cỡ đệm tai (S, M, L) đi kèm đảm bảo sự thoải mái tối đa khi đeo trong thời gian dài, đồng thời mang lại khả năng cách âm thụ động hiệu quả. Kết nối Bluetooth 5.0 siêu nhanh và ổn định giúp giảm thiểu độ trễ, cực kỳ quan trọng cho game thủ và những người thường xuyên xem video. Thời lượng pin ấn tượng lên đến 25,5 giờ (với hộp sạc) cho phép bạn sử dụng cả ngày mà không lo hết pin. Hộp sạc nhỏ gọn, hỗ trợ sạc nhanh và sạc không dây chuẩn Qi, mang lại sự tiện lợi tối đa. Tích hợp micro kép với công nghệ khử ồn AI giúp giọng nói của bạn luôn rõ ràng khi đàm thoại, ngay cả trong môi trường ồn ào. Ứng dụng Armory Crate cho phép tùy chỉnh EQ, cài đặt ANC, cập nhật firmware và cá nhân hóa trải nghiệm nghe theo sở thích riêng. Khả năng kháng nước IPX4 giúp tai nghe bền bỉ hơn trước mồ hôi và những cơn mưa nhỏ. Tai nghe Asus ROG Cetra True Wireless không chỉ là một thiết bị âm thanh, mà còn là người bạn đồng hành lý tưởng cho mọi hoạt động của bạn.',
        1, 'https://product.hstatic.net/200000722513/product/asus-rog-cetra-true-wireless-02_acded5b0105145d6beeb8762178ddd94.jpg'
    );
