INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Edifier không dây W830NB White', 'tai-nghe-edifier-khong-day-w830nb-white', 30,
        '{"Tên sản phẩm": "Edifier W830NB", "Màu sắc": "Trắng", "Loại tai nghe": "Over-ear (chụp tai)", "Công nghệ chống ồn": "Chống ồn chủ động (ANC)", "Phiên bản Bluetooth": "5.0", "Kích thước màng loa": "40mm", "Tần số phản hồi": "20Hz - 20kHz", "Trở kháng": "24Ω", "Độ nhạy": "94dB", "Thời lượng pin (ANC bật)": "Khoảng 33 giờ", "Thời lượng pin (ANC tắt)": "Khoảng 50 giờ", "Thời gian sạc": "Khoảng 1.5 giờ", "Cổng sạc": "USB Type-C", "Tích hợp micro": "Có", "Trọng lượng": "293g", "Chuẩn codec âm thanh": "aptX HD, aptX, SBC", "Chất liệu đệm tai": "Da protein"}',
        'Đánh giá chi tiết tai nghe Edifier không dây W830NB White\n\nTai nghe Edifier W830NB White, một trong những chiếc tai nghe không dây nổi bật đến từ thương hiệu Trung Quốc nổi tiếng với những sản phẩm âm thanh chất lượng cao.\n\nThiết kế hiện đại và sang trọng\nTai nghe Edifier W830NB White được thiết kế với vẻ ngoài thanh lịch, nhẹ nhàng. Màu trắng trang nhã cùng các chi tiết kim loại giúp chiếc tai nghe Edifier trở nên sang trọng, pha lẫn chút cứng cáp. Phần đệm tai nghe được sản xuất với chất liệu mềm mại và thân thiện với da người dùng, đem lại cảm giác thoải mái khi sử dụng trong thời gian dài. Cùng với đó, phần băng đô trên Edifier W830NB White cho phép bạn tùy chỉnh để phù hợp với trải nghiệm đeo cá nhân.\n\nKhả năng chống ồn chủ động thông minh\nMặc dù thuộc phân khúc tai nghe tầm trung, Edifier W830NB White vẫn sở hữu tính năng vô cùng được nhiều người dùng chú ý: khả năng chống ồn chủ động (ANC). Với công nghệ này, chiếc tai nghe sẽ giúp loại bỏ tiếng ồn môi trường bên ngoài để mang đến trải nghiệm âm thanh tốt nhất hoặc sự tập trung cao nhất cho công việc.\n\nChất lượng âm thanh vượt trội\nEdifier W830NB White trang bị công nghệ âm thanh tiên tiến, mang đến những âm thanh sống động nhất với nhịp điệu bass mạnh mẽ và âm treble trong trẻo thông qua driver 40mm. Chiếc tai nghe từ Edifier hỗ trợ nhiều định dạng âm thanh khác nhau và có khả năng tái tạo chi tiết, đưa bạn đến những giai điệu sống động.\n\nThời lượng pin ấn tượng\nTai nghe Edifier W830NB White có thể cung cấp thời lượng pin lên đến 94 giờ khi tắt tính năng ANC và 54 giờ khi tính năng này được kích hoạt. Đây là một con số ấn tượng đối với nhu cầu sử dụng giải trí trung bình. Điều này giúp Edifier W830NB White thoải mái đồng hành cùng bạn trong suốt những chuyến đi.\n\nKết nối tốc độ cùng Bluetooth 5.4\nTai nghe Edifier W830NB được trang bị công nghệ Bluetooth 5.4 tiên tiến, mang lại tốc độ và độ ổn định lý tưởng cho kết nối giữa sản phẩm và các thiết bị ghép nối như laptop, smartphone hay máy tính bảng. Qua đó, đảm bảo kết nối liền mạch và giảm thiểu tình trạng khó chịu do kết nối yếu của những chiếc tai nghe giá rẻ.\n\nĐiều khiển và tùy biến siêu tiện lợi\nEdifier W830NB White bố trí các nút điều khiển thông minh và tiện lợi, giúp bạn dễ dàng thao tác trong khi làm việc hay học tập. Đi kèm với chiếc tai nghe bluetooth, bạn sẽ được hỗ trợ thêm phần mềm EDIFIER ConneX. Đây sẽ là \"quản gia\" âm thanh trên Edifier W830NB White, mang đến đa dạng lựa chọn cá nhân hóa cho từng bài hát bạn nghe hay những bộ phim bạn xem.',
        1, 'https://product.hstatic.net/200000722513/product/830.727_6eacef2b54ae4c5c8d050e519a95c1a5.png'
    );
