INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Edifier không dây W800BT SE Black', 'tai-nghe-edifier-khong-day-w800bt-se-black', 30,
        '{"Model": "Edifier W800BT SE", "Loại tai nghe": "Over-ear", "Kết nối không dây": "Bluetooth 5.1", "Hồ sơ Bluetooth": "HFP, A2DP, AVRCP", "Tần số phản hồi": "20Hz - 20kHz", "Trở kháng": "32Ω", "Độ nhạy": "100dB", "Kích thước driver": "40mm", "Loại driver": "Neodymium", "Microphone": "Tích hợp", "Thời lượng pin (nghe nhạc)": "Khoảng 50 giờ", "Thời gian sạc": "Khoảng 2.5 giờ", "Cổng sạc": "USB-C", "Phạm vi hoạt động": "Lên đến 10m", "Màu sắc": "Đen", "Hỗ trợ codec Bluetooth": "SBC", "Trọng lượng": "217g", "Chất liệu đệm tai": "Da tổng hợp", "Tính năng nổi bật": "Có thể gập gọn, Hỗ trợ kết nối có dây 3.5mm"}',
        'Đánh giá chi tiết tai nghe Edifier không dây W800BT SE Black. Edifier là thương hiệu audio sở hữu các sản phẩm hướng đến người dùng phổ thông, dân văn phòng và cả game thủ. Mọi yêu cầu sẽ được tích hợp trên một thiết bị đa năng cho cả giải trí lẫn làm việc, đặc biệt là tai nghe máy tính.\n\nEdifier W800BT SE Black sẽ là một lựa chọn đáng chú ý trong tầm giá dưới 1 triệu đồng.\n\n**Thiết kế hiện đại, đề cao sự bền bỉ và thoải mái**\n\nEdifier W800BT SE sở hữu thiết kế ôm tai đặc trưng trên dòng tai nghe over-ear. Phần băng đô được tối ưu hóa giúp giảm áp lực lên đầu, cùng phần đệm tai sử dụng chất liệu mềm mại và thân thiện với làn da người dùng, mang lại trải nghiệm thoải mái khi sử dụng tai nghe trong thời gian dài.\n\nThiết kế thông minh cho phép người dùng linh hoạt điều chỉnh theo yêu cầu cá nhân, hạn chế cảm giác không thoải mái, đau tai hay bị ép chặt vào đầu khi đeo kính và sử dụng Edifier W800BT SE Black. Vào mùa hè hoặc khi sử dụng lâu trong môi trường nóng, chiếc tai nghe này hạn chế tình trạng bí, nóng khi làm việc và giải trí cùng PC Gaming hay laptop.\n\n**Chất lượng âm thanh ổn định**\n\nEdifier W800BT SE Black được trang bị driver động 40mm và màng loa phủ titan, mang lại âm thanh trung thực với âm trường rộng và dải bass mạnh mẽ. Chất âm lý tưởng cho nhu cầu nghe nhạc, xem phim, chơi game và học tập. Ngoài ra, công nghệ khử tiếng ồn thông minh bằng AI kết hợp cùng micro có độ nhạy tốt giúp chiếc tai nghe Edifier mang lại chất lượng cuộc gọi và họp online tốt hơn cho dân văn phòng và người làm việc từ xa.\n\n**Kết nối đơn giản**\n\nChiếc tai nghe không dây từ Edifier sử dụng Bluetooth V5.4 cho tốc độ kết nối nhanh chóng và ổn định. Ngoài ra, Edifier W800BT SE Black hỗ trợ kết nối đa điểm, cung cấp khả năng chuyển đổi linh hoạt giữa hai thiết bị mà không cần ngắt kết nối. Độ trễ thấp chỉ 0,06 giây mà Edifier W800BT SE Black mang lại đảm bảo trải nghiệm liền mạch khi chơi game, mượt mà khi xem video mà không bị trễ hình. Cuối cùng là khả năng hỗ trợ cổng USB Type-C trên chiếc tai nghe Edifier.\n\n**Liên tục sử dụng cùng khả năng sạc pin nhanh**\n\nVới thời lượng pin lên tới 40–45 giờ phát nhạc liên tục, Edifier W800BT SE Black sẽ đồng hành cùng bạn trong một ngày làm việc hoặc trên những chuyến đi đường dài. Ngoài ra, công nghệ sạc nhanh trên chiếc tai nghe cho phép sạc 15 phút để sử dụng thêm 8 giờ, vô cùng tiện lợi cho nhu cầu di chuyển liên tục.',
        1, 'https://product.hstatic.net/200000722513/product/3_1c356400926c459daf0b8393c466b0b0.png'
    );
